02458                                  element->GetBackgroundColor()->GetGreen(),
02459                                  element->GetBackgroundColor()->GetBlue(),
02460                                  element_listbox->IsPreSelect()?element->GetBackgroundColor()->GetAlpha()/2:element->GetBackgroundColor()->GetAlpha());
02461 
02462       GRP2DCOLOR_RGBA8  linecolor;
02463 
02464       GRP2DCOLOR_RGBA8  colorwhite(255, 255, 255, 255);
02465  
