02473                             element->GetYPosition()  + UI_SKINCANVAS_EDIT_MAXEDGE                                      ,
02474                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02475                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , element->GetRoundRect(), true);
02476         }
02477        else
