02481                             element->GetXPosition()  + element->GetBoundaryLine()->width  + UI_SKINCANVAS_EDIT_MAXEDGE , 
02482                             element->GetYPosition()  - element->GetBoundaryLine()->height - UI_SKINCANVAS_EDIT_MAXEDGE , true);
02483         }
02484                                       
02485       canvas->Vectorfont_GetConfig()->SetColor(&color);
02486       canvas->Vectorfont_GetConfig()->SetSize(element_listbox->GetSizeFont());  
02487       canvas->VectorFont_Printf(element->GetXPosition(), element->GetYPosition(), element_listbox->GetText()->Get());
02488     }
02489   
02490   PostDrawFunction(element, canvas, clip_rect, x_position, y_position);
02491 
02492   return true;
02493 }
