02495 
02507 bool UI_SKINCANVAS::Draw_ProgressBar(UI_ELEMENT* element)
02508 {
02509   if(!element) return false;  
02510 
02511   UI_ELEMENT_PROGRESSBAR* element_progressbar   = (UI_ELEMENT_PROGRESSBAR*)element;
02512   UI_ELEMENT*             element_progressrect  = element_progressbar->GetProgressRect();
02513   UI_ELEMENT_ANIMATION*   element_animation     = element_progressbar->Get_UIAnimation();
02514   UI_ELEMENT_TEXT*        element_text          = element_progressbar->Get_UIText();
02515   double                  x_position            = 0.0f;
02516   double                  y_position            = 0.0f;
02517   GRPCANVAS*              canvas                = GetCanvas();    
02518   static bool             visible               = false;
02519   XRECT                   clip_rect;                       
02520   
02521   if(!canvas) return false;
02522 
02523   PreDrawFunction(element, canvas, clip_rect, x_position, y_position, 1);
02524 
02525   if(element->MustReDraw()) 
02526     {
02527       if(element_progressrect)
02528         {
02529           GRP2DCOLOR_RGBA8  linecolor(element_progressbar->GetLineColor()->GetRed(), element_progressbar->GetLineColor()->GetGreen(), element_progressbar->GetLineColor()->GetBlue(), element_progressbar->GetLineColor()->GetAlpha());
02530           GRP2DCOLOR_RGBA8  color    (element->GetColor()->GetRed(), element->GetColor()->GetGreen(), element->GetColor()->GetBlue(), element->GetColor()->GetAlpha());      
02531           GRP2DCOLOR_RGBA8  bkgcolor (element->GetBackgroundColor()->GetRed(), element->GetBackgroundColor()->GetGreen(), element->GetBackgroundColor()->GetBlue(), element->GetBackgroundColor()->GetAlpha());
02532           
02533           canvas->SetLineColor(&linecolor);
02534           canvas->SetFillColor(&bkgcolor);
02535 
02536           if(element->GetRoundRect())
02537             {
02538               canvas->RoundRect(element_progressrect->GetXPosition(), 
02539                                 element_progressrect->GetYPosition(),
02540                                 element_progressrect->GetXPosition() + element_progressrect->GetBoundaryLine()->width  , 
02541                                 element_progressrect->GetYPosition() - element_progressrect->GetBoundaryLine()->height , element->GetRoundRect(), true);
02542             }
02543            else
02544             { 
02545               canvas->Rectangle(element_progressrect->GetXPosition(), 
02546                                 element_progressrect->GetYPosition(),
02547                                 element_progressrect->GetXPosition() + element_progressrect->GetBoundaryLine()->width  , 
02548                                 element_progressrect->GetYPosition() - element_progressrect->GetBoundaryLine()->height , true);
02549             }
02550 
02551           double widthpercent   = element_progressrect->GetBoundaryLine()->width;
02552           double heightpercent  = element_progressrect->GetBoundaryLine()->height;  
02553                                     
02554           if(element_progressbar->ContinuousCycle_Is())
02555             {                
02556               XDWORD xpos_segment = 0;
02557               XDWORD ypos_segment = 0;
02558            
02559               switch(element_progressbar->GetDirection())
02560                 {     
02561                   case UI_ELEMENT_TYPE_DIRECTION_UNKWOWN      :
02562                     case UI_ELEMENT_TYPE_DIRECTION_HORIZONTAL : widthpercent   = (element_progressrect->GetBoundaryLine()->width  * element_progressbar->ContinuousCycle_GetSizeSegment()) / 100.0f;   
02563                                                               xpos_segment   = element_progressbar->ContinuousCycle_GetNSteps();
02564                                                               xpos_segment  += element_progressbar->ContinuousCycle_GetStepSegment();
02565                                                               element_progressbar->ContinuousCycle_SetNSteps(xpos_segment);        
02566                                                                                                                           
02567                                                               if(xpos_segment < widthpercent) 
02568                                                                 {                                                                   
02569                                                                   widthpercent = xpos_segment;                                                                  
02570                                                                   xpos_segment = 0;                                                                                                                                                                                                                                                                                                                                           
02571                                                                 }                                                                
02572                                                                else
02573                                                                 {                                                                                                                                      
02574                                                                   if((xpos_segment + widthpercent) >= element_progressrect->GetBoundaryLine()->width) 
02575                                                                     {                                                                       
02576                                                                       widthpercent = (element_progressrect->GetBoundaryLine()->width - xpos_segment);                                                                       
02577                                                                       if(!widthpercent) 
02578                                                                         {
02579                                                                           xpos_segment = 0;
02580                                                                           element_progressbar->ContinuousCycle_SetNSteps(xpos_segment);                                                                          
02581                                                                         }
02582 
02583                                                                       xpos_segment -= (XDWORD)widthpercent;   
02584                                                                     }  
02585                                                                    else
02586                                                                     {
02587                                                                       xpos_segment -= (XDWORD)widthpercent;   
02588                                                                     }                                                                                                                                                                                                                       
02589                                                                 }
02590                                                                  
02591                                                                                                                                                                                       
02592                                                               break;
02593 
02594                     case UI_ELEMENT_TYPE_DIRECTION_VERTICAL     : heightpercent  = (element_progressrect->GetBoundaryLine()->height * element_progressbar->ContinuousCycle_GetSizeSegment()) / 100.0f;   
02595                                                               ypos_segment   = element_progressbar->ContinuousCycle_GetNSteps();
02596                                                               ypos_segment  += element_progressbar->ContinuousCycle_GetStepSegment();
02597                                                               element_progressbar->ContinuousCycle_SetNSteps(ypos_segment);        
02598                                                               
02599                                                               if(ypos_segment < heightpercent) 
02600                                                                 {                                                                   
02601                                                                   heightpercent = ypos_segment;                                                                  
02602                                                                   ypos_segment = 0;                                                                                                                                                                                                                                                                                                                                           
02603                                                                 }                                                                
02604                                                                else
02605                                                                 {                                                                                                                                      
02606                                                                   if((ypos_segment + heightpercent) >= element_progressrect->GetBoundaryLine()->height) 
02607                                                                     {                                                                       
02608                                                                       heightpercent = (element_progressrect->GetBoundaryLine()->height - ypos_segment);                                                                       
02609                                                                       if(!heightpercent) 
02610                                                                         {
02611                                                                           ypos_segment = 0;
02612                                                                           element_progressbar->ContinuousCycle_SetNSteps(ypos_segment);                                                                          
02613                                                                         }
02614 
02615                                                                       ypos_segment -= (XDWORD)heightpercent;   
02616                                                                     }  
02617                                                                    else
02618                                                                     {
02619                                                                       ypos_segment -= (XDWORD)heightpercent;   
02620                                                                     }                                                                                                                                                                                                                       
02621                                                                 }
02622                                                                  
02623                                                               break;
02624                 }
02625 
02626               canvas->SetFillColor(&color);
02627 
02628               if(element->GetRoundRect())
02629                 {
02630                   canvas->RoundRect(element_progressrect->GetXPosition() + xpos_segment, 
02631                                     element_progressrect->GetYPosition() + ypos_segment,
02632                                     element_progressrect->GetXPosition() + xpos_segment + widthpercent    , 
02633                                     element_progressrect->GetYPosition() - (ypos_segment + heightpercent) , element->GetRoundRect(), true);                   
02634 
02635                 }
02636                else
02637                 {
02638                   canvas->Rectangle(element_progressrect->GetXPosition() + xpos_segment, 
02639                                     element_progressrect->GetYPosition() + ypos_segment,
02640                                     element_progressrect->GetXPosition() + xpos_segment + widthpercent    , 
02641                                     element_progressrect->GetYPosition() - (ypos_segment + heightpercent) , true);                   
02642                 }
02643             }
02644            else
02645             {                
02646               switch(element_progressbar->GetDirection())
02647                 {     
02648                   case UI_ELEMENT_TYPE_DIRECTION_UNKWOWN      :
02649                     case UI_ELEMENT_TYPE_DIRECTION_HORIZONTAL : widthpercent   = (element_progressrect->GetBoundaryLine()->width  * element_progressbar->GetLevel()) / 100.0f;   break;
02650                     case UI_ELEMENT_TYPE_DIRECTION_VERTICAL     : heightpercent  = (element_progressrect->GetBoundaryLine()->height * element_progressbar->GetLevel()) / 100.0f;   break;
02651                 }
02652                
02653               canvas->SetFillColor(&color);
02654 
02655               if(element->GetRoundRect())
02656                 {
02657                   canvas->RoundRect(element_progressrect->GetXPosition(), 
02658                                     element_progressrect->GetYPosition(),
02659                                     element_progressrect->GetXPosition() + widthpercent   , 
02660                                     element_progressrect->GetYPosition() - heightpercent  , element->GetRoundRect(), true);   
02661                 }
02662                else
02663                 {
02664                   canvas->Rectangle(element_progressrect->GetXPosition(), 
02665                                     element_progressrect->GetYPosition(),
02666                                     element_progressrect->GetXPosition() + widthpercent   , 
02667                                     element_progressrect->GetYPosition() - heightpercent  , true);          
02668                 }             
02669             } 
02670 
02671           if(element_text) Draw(element_text);  
02672             
02673         }  
02674     
02675       if(element_animation) Draw(element_animation);                    
02676     }
02677 
02678   PostDrawFunction(element, canvas, clip_rect, x_position, y_position); 
02679     
02680   if(element_progressbar->ContinuousCycle_Is()) 
02681     {
02682       if(element_progressbar->ContinuousCycle_GetXTimer()->GetMeasureMilliSeconds() > element_progressbar->ContinuousCycle_GetTimeStep())
02683         {
02684           element->SetMustReDraw(true);
02685 
02686           element_progressbar->ContinuousCycle_GetXTimer()->Reset();
02687         }
02688     }
02689   
02690   return true;
02691 }
