02777 
02794 bool UI_SKINCANVAS::PreDrawFunction(UI_ELEMENT* element, GRPCANVAS* canvas, XRECT& clip_rect, double& x_position, double& y_position, XDWORD edge)
02795 {
02796   if(!canvas) return false;
02797 
02798   x_position = element->GetXPosition();
02799   y_position = element->GetYPosition();
02800 
02801   if(element->GetFather()) GetAddPositionScrollSteps(element->GetFather(), x_position, y_position);
02802 
02803   element->SetXPositionWithScroll(x_position);
02804   element->SetYPositionWithScroll(y_position);
02805  
02806   bool createarea = false;
02807   if(element->MustReDraw()) 
02808     {
02809       createarea = true;
02810       if(GetRebuildAreaByElement(element)) createarea = false;          
02811     }
02812 
02813   if(createarea)
02814     {     
02815       //XTRACE_PRINTCOLOR(XTRACE_COLOR_BLUE, __L("Add area: level [%d] [%s] "), element->GetZLevel(), element->GetName()->Get());
02816          
02817       CreateRebuildArea(x_position - edge, 
02818                         y_position - element->GetBoundaryLine()->height - edge, 
02819                         element->GetBoundaryLine()->width   + (edge * 2),
02820                         element->GetBoundaryLine()->height  + (edge * 2), element);                    
02821     }
02822 
02823   if(!element->GetFather())
02824     {
02825       //if(dynamic_cast<UI_PROPERTY_SCROLLEABLE*>(element))
02826         {
02827           canvas->GetClipBox(clip_rect);      
02828           canvas->SetClipBox(x_position - 1 , 
02829                              y_position     ,
02830                              x_position + element->GetBoundaryLine()->width  , 
02831                              y_position - element->GetBoundaryLine()->height - 1);       
02832         }
02833     }
02834   
02835   return true;
02836 }
02837 
02838 
02854 bool UI_SKINCANVAS::PostDrawFunction(UI_ELEMENT* element, GRPCANVAS* canvas, XRECT& clip_rect, double x_position, double y_position)
02855 {  
02856   element->SetMustReDraw(false);
02857 
02858   if(!element->GetFather())
02859     {  
02860       //if(dynamic_cast<UI_PROPERTY_SCROLLEABLE*>(element))
02861         {
02862           canvas->SetClipBox(clip_rect);
02863         }
02864     }
02865   
02866   #ifdef USERINTERFACE_DEBUG 
02867     Debug_Draw(element, x_position, y_position);            
02868   #endif
02869 
02870   return true;
02871 }
02872 
02873 
02893 double UI_SKINCANVAS::TextBox_SizeLine(UI_ELEMENT_TEXTBOX* element_textbox, GRPCANVAS* canvas, double x_position, double y_position, int nline, XSTRING& characterstr, XDWORD index_char, XVECTOR<UI_SKIN_TEXTBOX_PART*>& parts, XVECTOR<UI_SKIN_TEXTBOX_IMAGE*>* images)
02894 {
02895   XSTRING _characterstr;
02896   XDWORD  y_pos    = 0;    
02897   double  sizeline = 0;
02898 
02899   _characterstr = characterstr;
02900                                
02901   for(XDWORD c=0; c<parts.GetSize(); c++)
02902     {
02903       UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(c);
02904       if(textbox_part->GetLineNumber() == nline)
02905         {
02906           sizeline += canvas->VectorFont_GetWidth(textbox_part->GetText()->Get());    
02907           y_pos     = textbox_part->GetYPos();    
02908         }                                      
02909     }
02910 
02911 
02912   _characterstr.Add(element_textbox->GetText()->Get()[index_char]); 
02913   sizeline += canvas->VectorFont_GetWidth(_characterstr.Get());  
02914 
02915   if(images)
02916     {
02917       for(XDWORD c=0; c<images->GetSize(); c++)
02918         {
02919           UI_SKIN_TEXTBOX_IMAGE* textbox_image = images->Get(c);
02920           if(textbox_image)
02921             {
02922               bool isinbox = textbox_image->GetBoundaryLine()->IsWithin((XDWORD)(x_position + sizeline), (XDWORD)(y_pos));        
02923               if(isinbox) 
02924                 {                         
02925                   sizeline += textbox_image->GetBoundaryLine()->width;
02926                 }            
02927             }                                      
02928         }
02929     }
02930 
02931   return sizeline;
02932 }
02933 
02934 
02951 bool UI_SKINCANVAS::TextBox_GenerateLines(UI_ELEMENT_TEXTBOX* element_textbox, GRPCANVAS* canvas, double x_position, double y_position, XVECTOR<UI_SKIN_TEXTBOX_PART*>& parts, XVECTOR<UI_SKIN_TEXTBOX_IMAGE*>& images)
02952 {
02953   double                  x_text_position = x_position;
02954   double                  y_text_position = y_position - element_textbox->GetBoundaryLine()->height + (double)canvas->VectorFont_GetHeight(__L("A"));  
02955   XDWORD                  index_char      = 0;  
02956   UI_COLOR                actual_color;
02957   XDWORD                  nline           = 1;
02958   double                  lineheight      = (canvas->VectorFont_GetHeight(__L("A")) + element_textbox->GetLineSpacing());
02959   XSTRING                 text;
02960   UI_SKIN_TEXTBOX_PART*   textbox_part    = NULL;
02961   bool                    outlimit        = false;
02962   bool                    newpart         = false;  
02963 
02964   text = element_textbox->GetText()->Get();
02965   if(text.IsEmpty()) return false;
02966 
02967   actual_color.CopyFrom(element_textbox->GetColor());
02968 
02969   textbox_part = new UI_SKIN_TEXTBOX_PART();
02970   if(!textbox_part) return false;
02971    
02972   textbox_part->SetLineNumber(nline);
02973   textbox_part->SetXPos((XDWORD)x_text_position);  
02974   textbox_part->SetYPos((XDWORD)y_text_position);  
02975   textbox_part->GetColor()->CopyFrom(&actual_color);
02976   
02977   while(index_char < text.GetSize() && (!outlimit))  
02978     {
02979       XCHAR character = text.Get()[index_char];
02980 
02981       switch(character)
02982         {
02983           case __C('\n')  : x_text_position  = x_position;      
02984                             y_text_position += lineheight;                
02985                             nline++;
02986                             newpart = true;     
02987 
02988                             index_char++;                            
02989                             break;
02990 
02991           case __C('!')   : { int indexstart = index_char+1;
02992                               if(text.Get()[indexstart] == __C('['))
02993                                 {                
02994                                   int type;
02995                 
02996                                   int indexend = text.FindCharacter(__C(']'), indexstart);
02997                                   if(indexend != XSTRING_NOTFOUND)
02998                                     {                                                                      
02999                                       XCHAR*    keywords[] = {  __L("COLOR") ,
03000                                                                 __L("IMAGE") ,
03001                                                                 __L("END") ,
03002                                                              };
03003                                       XSTRING   keyword;
03004                                       bool      found_keyword = false;  
03005                                                                                     
03006                                       text.Copy(indexstart+1, indexend, keyword);    
03007 
03008                                       for(type=0; type<(sizeof(keywords)/sizeof(XCHAR*)); type++)
03009                                         {
03010                                           int keywordindex = keyword.Find(keywords[type], true);
03011                                           if(!keywordindex)
03012                                             {
03013                                               XSTRING keyword_params;
03014                                               XSTRING string;        
03015                                               
03016                                               found_keyword = true;  
03017 
03018                                               string = keywords[type];
03019                                               keywordindex += string.GetSize()+1;
03020 
03021                                               keyword.Copy(keywordindex, keyword_params);
03022                                               keyword_params.DeleteCharacter(__C(' '));
03023 
03024                                               switch(type)
03025                                                 {
03026                                                   case  0 : actual_color.SetFromString(keyword_params);
03027                                                             newpart = true;
03028                                                             break;      
03029 
03030                                                   case  1 : { UI_SKIN_TEXTBOX_IMAGE* image = new UI_SKIN_TEXTBOX_IMAGE();
03031                                                               if(image)
03032                                                                 {
03033                                                                   image->GetName()->Set(keyword_params);                                                                  
03034 
03035                                                                   UI_ANIMATION* animation = GEN_USERINTERFACE.GetOrAddAnimationCache(__L(""), image->GetName()->Get());
03036                                                                   if(animation)
03037                                                                     {
03038                                                                       image->SetAnimation(animation); 
03039                                                                       
03040                                                                       image->GetBoundaryLine()->x       = x_text_position;
03041                                                                       image->GetBoundaryLine()->y       = y_text_position - (int)(canvas->VectorFont_GetHeight(__L("A"))); 
03042                                                                       image->GetBoundaryLine()->width   = animation->GetBitmap()->GetWidth();
03043                                                                       image->GetBoundaryLine()->height  = animation->GetBitmap()->GetHeight(); // + lineheight; // + (int)(canvas->VectorFont_GetHeight(__L("A"))); 
03044                                                                       
03045                                                                       images.Add(image);                                                                     
03046                                                                     }
03047                                                                 }    
03048                                                             }
03049                                                             break;        
03050 
03051                                                   case  2 : for(int d=0; d<(sizeof(keywords)/sizeof(XCHAR*)); d++)
03052                                                               {
03053                                                                 if(!keyword_params.Compare(keywords[d], true))
03054                                                                   {
03055                                                                     switch(d)
03056                                                                       {
03057                                                                         case  0 : actual_color.CopyFrom(element_textbox->GetColor());
03058                                                                                   newpart = true;
03059                                                                                   break;
03060 
03061                                                                         case  1 : break;  
03062                                                                       }                                                                    
03063                                                                   }
03064                                                               }
03065                                                             break;                                                          
03066                                                 }
03067                                               
03068                                               break;
03069                                             }
03070                                         }
03071                                                                   
03072                                       if(found_keyword) 
03073                                         {    
03074                                           text.DeleteCharacters(index_char, (keyword.GetSize() + 3));                                         
03075                                           
03076                                                         
03077                                           if(type == 1 && (indexstart > 1))
03078                                             {
03079                                               indexstart = -2;
03080 
03081                                               if(text.Get()[indexstart] != __C(' '))  
03082                                                 {
03083                                                   text.Insert(__L(" "), indexstart);                                                                                                  
03084                                                 }
03085                                             }
03086                                             
03087                                           break;
03088                                         }
03089                                     }
03090                                 } 
03091                             }
03092 
03093                   default : { XSTRING characterstr;                              
03094                               characterstr.Add(character);
03095                               
03096                               x_text_position += (canvas->VectorFont_GetWidth(characterstr.Get())); 
03097 
03098                               UI_BOUNDARYLINE   boundaryline;
03099                               bool              isinbox    = false;
03100                               bool              isinimage  = false;  
03101                               double            imagewidth = 0.0f;
03102                                 
03103                               boundaryline.x      = element_textbox->GetXPosition();
03104                               boundaryline.y      = element_textbox->GetYPosition();
03105                               boundaryline.width  = element_textbox->GetBoundaryLine()->width;
03106                               boundaryline.height = element_textbox->GetBoundaryLine()->height;
03107 
03108                               isinbox = boundaryline.IsWithin((XDWORD)(x_text_position), (XDWORD)(y_text_position));                                                           
03109                               if(isinbox)
03110                                 {
03111                                   for(XDWORD c=0; c<images.GetSize(); c++)
03112                                     {                                      
03113                                       UI_SKIN_TEXTBOX_IMAGE* textbox_image = images.Get(c);
03114                                       if(textbox_image)
03115                                         {                                                                            
03116                                           isinimage = boundaryline.IsWithin((XDWORD)(x_text_position), (XDWORD)(y_text_position - textbox_image->GetBoundaryLine()->height - (int)(canvas->VectorFont_GetHeight(__L("A"))) + 1));        
03117                                           if(isinimage) 
03118                                             {
03119                                               textbox_image->GetLineNumbers()->Add(nline);
03120                                               imagewidth = textbox_image->GetBoundaryLine()->width;
03121                                               break;                                          
03122                                             }
03123                                         }
03124                                     }        
03125                                 }
03126                                       
03127                               if(isinbox)
03128                                 {      
03129                                   if(isinimage)
03130                                     {
03131                                       if(element_textbox->IsWordClipping())
03132                                         {
03133                                           if(character != __C(' '))
03134                                             {
03135                                               while(!textbox_part->GetText()->IsEmpty())
03136                                                 {  
03137                                                   if(textbox_part->GetText()->Get()[textbox_part->GetText()->GetSize()-1] == __C(' ')) break;
03138                                                   textbox_part->GetText()->DeleteLastCharacter();
03139                                                   index_char--;
03140                                                 }                                                 
03141                                             }
03142                                         }
03143 
03144                                       x_text_position += imagewidth;    
03145                                       newpart          = true;                                          
03146                                     }
03147                                    else     
03148                                     {                                        
03149                                       textbox_part->GetText()->Add(characterstr);
03150                                       index_char++;
03151                                     }
03152                                 }
03153                                else
03154                                 {  
03155                                   if(element_textbox->IsWordClipping())
03156                                     {      
03157                                       while(!textbox_part->GetText()->IsEmpty())
03158                                         {
03159                                           if(textbox_part->GetText()->Get()[textbox_part->GetText()->GetSize()-1] == __C(' ')) break;
03160                                           textbox_part->GetText()->DeleteLastCharacter();
03161                                           index_char--;
03162                                         }          
03163                                     }
03164                                     
03165                                   x_text_position  = x_position;      
03166                                   y_text_position += lineheight; 
03167                                   if(y_text_position > (y_position + lineheight)) 
03168                                     {
03169                                       outlimit = true;
03170                                       break;
03171                                     }  
03172                
03173                                   nline++;
03174 
03175                                   if(!textbox_part->GetText()->IsEmpty()) 
03176                                     {
03177                                       newpart = true;                                      
03178                                     }
03179                                    else
03180                                     {
03181                                       textbox_part->SetLineNumber(nline);
03182                                       textbox_part->SetXPos((XDWORD)x_text_position);  
03183                                       textbox_part->SetYPos((XDWORD)y_text_position);  
03184                                     }
03185                                 }                               
03186                             }
03187                             break;
03188         }
03189 
03190 
03191       if(newpart)
03192         {
03193           if(!textbox_part->GetText()->IsEmpty())    
03194             {
03195               textbox_part->SetWidth((XDWORD)canvas->VectorFont_GetWidth(textbox_part->GetText()->Get()));
03196               textbox_part->SetHeight((XDWORD)canvas->VectorFont_GetHeight(__L("A")));
03197               parts.Add(textbox_part);  
03198 
03199               textbox_part = new UI_SKIN_TEXTBOX_PART();
03200               if(!textbox_part) return false;
03201             }
03202    
03203           textbox_part->SetLineNumber(nline);
03204           textbox_part->SetXPos((XDWORD)x_text_position);  
03205           textbox_part->SetYPos((XDWORD)y_text_position);  
03206           textbox_part->GetColor()->CopyFrom(&actual_color);
03207 
03208           newpart = false;
03209         }
03210     }
03211 
03212   if(outlimit)
03213     {
03214       delete textbox_part;
03215       textbox_part = NULL;
03216     }
03217    else
03218     {
03219       parts.Add(textbox_part);  
03220     }
03221 
03222   XVECTOR<UI_SKIN_TEXTBOX_PART*>    checkparts;
03223   XVECTOR<UI_SKIN_TEXTBOX_IMAGE*>   checkimages;
03224   XDWORD                            maxnlines   = 0;
03225 
03226   for(XDWORD c=0; c<parts.GetSize(); c++)
03227     {
03228       UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(c);
03229       if(textbox_part) maxnlines = __MAX(maxnlines, textbox_part->GetLineNumber());  
03230     }
03231 
03232   for(XDWORD nl=0; nl<maxnlines; nl++)
03233     {     
03234       for(XDWORD p=0; p<parts.GetSize(); p++)
03235         {          
03236           UI_SKIN_TEXTBOX_PART* textbox_part = parts.Get(p);        
03237           if(textbox_part) 
03238             {
03239               if(textbox_part->GetLineNumber() == nl+1) checkparts.Add(textbox_part);
03240             }
03241         }
03242 
03243       for(XDWORD p=0; p<images.GetSize(); p++)
03244         {          
03245           UI_SKIN_TEXTBOX_IMAGE* textbox_image = images.Get(p);        
03246           if(textbox_image) 
03247             {
03248               if(textbox_image->IsInLine(nl+1))  checkimages.Add(textbox_image);
03249             }
03250         }
03251 
03252       if(checkparts.GetSize())
03253         {  
03254           switch(element_textbox->GetTextAlignment())
03255             {
03256               /*                                      
03257                                       default   :
03258               case UI_ELEMENT_TYPE_ALIGN_LEFT     : textbox_part->SetXPos((XDWORD)x_text_position);
03259                                                   break;                                        
03260 
03261               case UI_ELEMENT_TYPE_ALIGN_RIGHT  :   { XDWORD shift = (XDWORD)(element_textbox->GetBoundaryLine()->width - canvas->VectorFont_GetWidth(characterstr.Get()));
03262                                                     textbox_part->SetXPos((XDWORD)(x_text_position) + shift);
03263                                                   }
03264                                                   break;                                                                                                  
03265 
03266               case UI_ELEMENT_TYPE_ALIGN_CENTER : { XDWORD shift = (XDWORD)(element_textbox->GetBoundaryLine()->width - canvas->VectorFont_GetWidth(characterstr.Get()))/2;
03267                                                     textbox_part->SetXPos((XDWORD)(x_text_position) + shift);
03268                                                   }
03269                                                   break;                                
03270               */  
03271 
03272 
03273                                       default   : break;
03274 
03275               case UI_ELEMENT_TYPE_ALIGN_LEFT     : break;                                      
03276 
03277               case UI_ELEMENT_TYPE_ALIGN_RIGHT  :   break;                                                                                                
03278 
03279               case UI_ELEMENT_TYPE_ALIGN_CENTER : if(checkimages.GetSize())
03280                                                     {
03281                                                       XDWORD first_size = 0;
03282 
03283                                                       UI_SKIN_TEXTBOX_IMAGE* first_image = checkimages.Get(0);                                                                  
03284                                                       if(first_image)
03285                                                         {
03286                                                           for(XDWORD p=0; p<checkparts.GetSize(); p++)
03287                                                             {          
03288                                                               UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03289                                                               if(textbox_part)
03290                                                                 {
03291                                                                   if(((textbox_part->GetXPos() + textbox_part->GetWidth()) <= first_image->GetBoundaryLine()->x) &&
03292                                                                      (textbox_part->GetXPos() >= (first_image->GetBoundaryLine()->x + first_image->GetBoundaryLine()->width)))
03293                                                                     {
03294                                                                       first_size += textbox_part->GetWidth();
03295                                                                     }
03296                                                                 }  
03297                                                             }
03298 
03299                                                           XDWORD shift = (XDWORD)(first_image->GetBoundaryLine()->x - element_textbox->GetBoundaryLine()->x - first_size);    
03300                                                           
03301                                                           for(XDWORD p=0; p<checkparts.GetSize(); p++)
03302                                                             {          
03303                                                               UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03304                                                               if(textbox_part)
03305                                                                 {
03306                                                                   if((textbox_part->GetXPos() + textbox_part->GetWidth()) <= first_image->GetBoundaryLine()->x)
03307                                                                     {
03308                                                                       textbox_part->SetXPos(textbox_part->GetXPos() + first_size);
03309                                                                     }
03310                                                                 }  
03311                                                             }
03312 
03313                                                         }
03314                                                     }
03315                                                    else
03316                                                     {
03317                                                       XDWORD sizealltext = 0;
03318                                                       for(XDWORD p=0; p<checkparts.GetSize(); p++)
03319                                                         {          
03320                                                            UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03321                                                            if(textbox_part) sizealltext += textbox_part->GetWidth();
03322                                                         }
03323                                           
03324                                                       XDWORD shift = (XDWORD)(element_textbox->GetBoundaryLine()->width - sizealltext)/2;    
03325 
03326                                                       for(XDWORD p=0; p<checkparts.GetSize(); p++)
03327                                                         {          
03328                                                            UI_SKIN_TEXTBOX_PART* textbox_part = checkparts.Get(p);        
03329                                                            if(textbox_part) textbox_part->SetXPos(textbox_part->GetXPos()+shift); 
03330                                                         }                                                             
03331                                                     }       
03332                                                   break;                                                   
03333             }
03334             
03335   
03336         }
03337 
03338       checkparts.DeleteAll();
03339       checkimages.DeleteAll();
03340                  
03341     }
03342   
03343 
03344   return true;
03345 }
