03349 
03363 bool UI_SKINCANVAS::Debug_Draw(UI_ELEMENT* element, double x_position, double y_position)
03364 {  
03365   GRPCANVAS* canvas = GetCanvas();
03366   if(!canvas) return false;
03367   
03368   GRP2DCOLOR_RGBA8  colorred(255, 0, 0);
03369   GRP2DCOLOR_RGBA8  colorblue(0, 0, 255);
03370  
03371   canvas->SetLineWidth(1.0f);
03372   canvas->SetLineColor(&colorblue);  
03373   canvas->Circle(x_position, y_position, 5);               
03374 
03375   canvas->SetLineWidth(1.0f);
03376   canvas->SetLineColor(&colorred);
03377   canvas->Rectangle(x_position ,  
03378                     y_position ,
03379                     x_position + (element->GetBoundaryLine()->width), 
03380                     y_position - (element->GetBoundaryLine()->height));
03381   return true;
03382 }
03383 #endif
