00535 UI_ANIMATION* UI_VIRTUALKEYBOARD::AddImageCache(XCHAR* name, XCHAR* resource)
00536 {
00537   UI_ANIMATION* animation = NULL;
00538 
00539   if(!skin) return NULL;
