00543 
00544   animation = GEN_UI_ANIMATIONS.Get(resource);
00545   if(animation) return animation;
00546 
00547   animation = new UI_ANIMATION();
00548   if(!animation) return NULL;
00549   
00550   XSTRING resourcename;
00551   bool    status = false;
00552 
00553   resourcename = resource;
00554 
00555   switch(skin->GetDrawMode())
00556     {
00557       case UI_SKIN_DRAWMODE_UNKNOWN   : break;
00558 
00559       case UI_SKIN_DRAWMODE_CANVAS    : { UI_SKINCANVAS* skincanvas = (UI_SKINCANVAS*)skin; 
00560                                           if(skincanvas) status = animation->LoadFromFile(resourcename, skincanvas->GetCanvas()->GetMode());                                                                           
00561                                         }
00562                                         break;
00563 
00564       case UI_SKIN_DRAWMODE_CONTEXT   : break;
00565     } 
00566 
00567   if(status)   
00568     {      
00569       animation->GetName()->Set(name);  
00570       animation->GetResource()->Set(resource);
00571 
00572       GEN_UI_ANIMATIONS.Add(animation);
00573 
00574       return animation;
00575     }
00576   
00577   delete animation;
00578 
00579   return NULL;  
