00833        else 
00834         {
00835           keyinfo.xpos  = xpos +  (((keyinfo.width  * 10) -  (keyinfo.width  * 7)) /2);
00836         }
00837 
00838       keyinfo.ypos -= keyinfo.height;
00839 
00840     } while(row < 3);
00841 
00842  
00843   GRPBITMAP* spaceimage = LoadKeyImage(__L("virtualkbd/space_key.png"));
00844   if(spaceimage)
00845     {
00846       keyinfo.width   = spaceimage->GetWidth();
00847       keyinfo.height  = spaceimage->GetHeight();
00848       keyinfo.xpos    = (main_form->GetBoundaryLine()->width - keyinfo.width)/2;
00849       AddKeyButton(__L("SPACE"), NULL, __L("virtualkbd/space_key.png"), keyinfo);
00850       
00851       //delete spaceimage;
00852     }  
00853  
00854   keyinfo.width     = key_normal_width;
00855   keyinfo.height    = key_normal_height;
00856 
00857   keyinfo.xpos      = xpos + (key_normal_width *2);  
00858   AddKeyButton(__L("ARROWLEFT"), NULL, __L("virtualkbd/arrowleft_key.png"), keyinfo);
00859 
00860 
00861   keyinfo.xpos      = xpos + (key_normal_width * 7);  
00862   AddKeyButton(__L("ARROWRIGHT"), NULL, __L("virtualkbd/arrowright_key.png"), keyinfo);
00863 
00864 
00865   keyinfo.xpos      = xpos;
00866   keyinfo.ypos     += key_normal_height;
00867   AddKeyButton(__L("CAPS"), NULL, __L("virtualkbd/caps_key.png"), keyinfo);
00868 
00869   keyinfo.xpos      = xpos;
00870   keyinfo.ypos     -= key_normal_height;
00871   AddKeyButton(__L("SYMBOLS"), NULL, __L("virtualkbd/symbols_key.png"), keyinfo);
00872 
00873   keyinfo.xpos      = xpos + (key_normal_width * 9); 
00874   keyinfo.ypos     += key_normal_height;
00875   AddKeyButton(__L("BACK"), NULL, __L("virtualkbd/back_key.png"), keyinfo);
00876 
00877   keyinfo.xpos      = xpos + (key_normal_width * 9); 
00878   keyinfo.ypos     -= key_normal_height;
00879   AddKeyButton(__L("RETURN"), NULL, __L("virtualkbd/return_key.png"), keyinfo);
00880     
00881   if(skin) skin->CalculeBoundaryLine_AllElements(main_form, true);
00882   
00883   return true;
00884 }
