00896 bool UI_VIRTUALKEYBOARD::DeleteAllKeys()
00897 {
00898   if(main_form->GetComposeElements()->IsEmpty()) return false;
00899 
00900   main_form->GetComposeElements()->DeleteContents();
00901   main_form->GetComposeElements()->DeleteAll();
00902 
00903   return true;
00904 }
