01116       XDWORD integerdec = 0;
01117 
01118       for(int c=0; c<data.GetSize(); c++)
01119         {
01120           integerdec |= data.Get()[c];
01121           if(c != data.GetSize() -1) integerdec <<= 8;
01122         }   
01123 
01124       string.Format(__L("%d"), integerdec);
01125     }
01126    else
01127     { 
01128       for(int c=0; c<data.GetSize(); c++)
01129         {
01130           string.AddFormat(__L("%02X"), data.Get()[c]);
01131         }
01132     }
01133   
01134   variant = string;
01135 
01136   return true;
01137 }
