01224 bool XBER::ConvertToObjetIdentifier(XBUFFER& data, XVARIANT& variant)
01225 {  
01226   XSTRING string;      
01227   int     length  = data.GetSize();
01228   XBYTE*  bits    = data.Get();
01229  
01230   if(length < 2 && !(length == 1 && bits[0] == 0))
01231     {
01232       return false; 
01233     }
01234 
01235   string.Format(__L("%d.%d"), (data.Get()[0]/40), (data.Get()[0]%40));
01236    
01237   size_t i = 0;
01238   while(i != length - 1)
01239     {
01240       XDWORD component = 0;
01241 
01242       while(i != length - 1)
01243         {
01244           ++i;
01245  
01246           if(component >> (32-7))
01247             {
01248               return false;      
01249             }
01250  
01251           component = (component << 7) + (bits[i] & 0x7F);
01252  
01253           if(!(bits[i] & 0x80))
01254             {
01255               break;
01256             }
01257         }
01258 
01259       string.AddFormat(__L(".%d"), component);
01260 
01261     }
01262 
01263   variant = string;
01264 
01265   return true;
01266 }
