01329 bool XBER::CalculeSize(XBUFFER& buffer, XDWORD& sizedataber, XBYTE& sizehead)
01330 {  
01331   int mode = -1;
01332   
01333   if(buffer.IsEmpty()) return false;
01334 
01335   if(!(buffer.Get()[1] & 0x80))
01336     {
01337       if(!(buffer.Get()[1]))
01338         {
01339           if(tagtype == XBER_TAGTYPE_NULL)            
01340                  mode = 0;             
01341             else mode = 3;
01342             
01343         } else mode  = 0;
01344 
01345     } else mode = 2;  
01346     
01347 
01348   switch(mode)
01349     {
01350       case  0 : // Short mode              
01351                 sizedataber = buffer.Get()[1];              
01352                 sizehead    = 2;
01353                 break;
01354 
01355       case  2 : // Long mode;
01356                 { 
01357                   int sizeofsize = (buffer.Get()[1] & 0x7F);
01358                   sizehead       = 2 + sizeofsize;
01359 
01360                   if(sizeofsize > 4)  return false;
01361                   if(!sizeofsize)     return false;                    
01362                 
01363                   memcpy((XBYTE*)&sizedataber, (XBYTE*)&buffer.Get()[2], sizeofsize);   
01364                   SWAPDWORD(sizedataber);
01365 
01366                   XBYTE bitsrotate = (32-(sizeofsize*8));
01367                   sizedataber >>= bitsrotate; 
01368                 }
01369                 break;
01370 
01371       case  3 : // Indefinite mode;                               
01372                 sizehead    = 2;
01373                 sizedataber = 0;
01374                 /*
01375                 for(XDWORD c=2; c<data.GetSize()-1; c++)
01376                   {
01377                     if(!data.Get()[c] && !data.Get()[c+1])  
01378                       {
01379                         sizedataber++;
01380                       }
01381                   }  
01382                 */
01383                 break;
01384 
01385       default : break;
01386 
01387     }
01388 
01389   return true;
01390 }
