01405 bool XBER::CodeBigNumber(XDWORD number, XBUFFER& data)
01406 {
01407   data.Delete();
01408 
01409   int value = number;
01410   int length = 0;
01411 
01412   if(value >= (268435456))
01413     { // 2 ^ 28
01414       length = 5;
01415     }
01416    else
01417     {
01418       if(value >= (2097152))
01419         { // 2 ^ 21
01420           length = 4;
01421         }
01422        else
01423         {
01424           if(value >= 16384)
01425             { // 2 ^ 14
01426               length = 3;
01427             }
01428            else
01429             {
01430               if(value >= 128)
01431                 { // 2 ^ 7
01432                   length = 2;
01433                 }
01434                else
01435                 {
01436                   length = 1;
01437                 }
01438             }
01439         }
01440     }
01441 
01442 
01443   int j = 0;
01444   for(j = length - 1; j >= 0; j--)
01445    {
01446      if(j)
01447       {
01448         int p = ((value >> (7 * j)) & 0x7F) | 0x80;
01449         data.Add((XBYTE)p);
01450       }
01451      else
01452       {
01453         int p = ((value >> (7 * j)) & 0x7F);
01454         data.Add((XBYTE)p);
01455       }
01456     }
01457 
01458   return true;
01459  }
