01462 
01475 bool XBER::CodeSize(XDWORD integer, XBUFFER& data)
01476 {
01477   data.Delete();
01478 
01479   XBYTE size = 0;
01480 
01481   if(integer&0xFF800000)
01482     {
01483       size = 4;
01484     }
01485    else
01486     {
01487       if(integer&0x00FF8000)
01488         {
01489           size = 3;
01490         }
01491        else
01492         {
01493           if(integer&0x0000FF80)
01494             {
01495               size = 2;
01496             }
01497            else
01498             {
01499               size = 1;
01500             }
01501         }
01502     }
01503 
01504   if(!size) return false;
01505 
01506   if(size>1) data.Add((XBYTE)((size-1)|0x80));
01507 
01508   for(int c=(size-data.GetSize()-1);c>=0;c--)
01509     {
01510       XBYTE byte = (XBYTE)((c?(integer>>(c*8)):(integer))&0xFF);
01511 
01512       data.Add((XBYTE)byte);
01513     }
01514 
01515   return true;
01516  }
