01550                   datestr.Format(__L("%02d%c%02d%c%04d"), day, separator, month, separator, year);
01551                 }
01552             }
01553         }
01554     }
01555 
01556   if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01557     {
01558       timestr.Format(__L("%02d:%02d:%02d"), hours, minutes, seconds);
01559     }
01560    else  
01561     {
01562       timestr.Format(__L("%02d:%02d"), hours, minutes);
01563     }
01564 
01565   if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHMILLISECONDS))
01566     {
01567       timestr.AddFormat(__L(".%03d"), milliseconds);
01568     }  
01569 
01570   if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTTIME))
01571     {
01572       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME))
01573         {
01574           string += timestr;
01575         }
01576 
01577       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE))
01578         {
01579           if(!string.IsEmpty()) string += __L(" ");
01580           string += datestr;
01581         }
01582     }
01583    else
01584     {
01585       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE)) 
01586         {
01587           string += datestr;
01588         }
01589 
01590       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME))
01591         {
01592           if(!string.IsEmpty()) string += __L(" ");
01593           string += timestr;
01594         }
01595     }
01596 
01597   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDAYOFWEEK))
01598     {
01599       if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDAYOFWEEK))
01600         {
01601           XSTRING dayofweek;
01602           XSTRING string2;
01603 
01604           GetDayOfWeekString(dayofweek);
01605 
01606           string2 += dayofweek;
01607 
01608           if(!string.IsEmpty()) string2 += __L(" ");
01609 
01610           string2 += string;
01611 
01612           string = string2;
01613         }
01614        else
01615         {
01616           XSTRING dayofweek;
01617 
01618           GetDayOfWeekString(dayofweek);
01619 
01620           if(!string.IsEmpty()) string += __L(" ");
01621           string += dayofweek;
01622         }
01623     }
01624 
01625   return true;
01626 }
