01628 
01641 bool XDATETIME::GetDateTimeToStringISO8601(XDWORD modificator, XSTRING& string)
01642 {
01643   string.Empty();
01644   
01645   XSTRING datestr;
01646   XSTRING timestr;
01647   XCHAR   dateseparator = __C('-');
01648   XCHAR   timeseparator = __C(':');
01649 
01650   if(!islocal) 
01651     {
01652       modificator |= XDATETIME_FORMAT_ISO8601_UTC;
01653     }
01654 
01655   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_BASIC))
01656     {
01657       datestr.Format(__L("%04d%02d%02d"), year, month, day);
01658     
01659       if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01660         {
01661           timestr.Format(__L("%02d%02d%02d"), hours, minutes, seconds);
01662         }
01663        else  
01664         {
01665           timestr.Format(__L("%02d%02d"), hours, minutes);
01666         }
01667     }
01668    else
01669     {
01670       datestr.Format(__L("%04d%c%02d%c%02d") , year, dateseparator, month, dateseparator, day);
01671     
01672       if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01673         {
01674           timestr.Format(__L("%02d%c%02d%c%02d"), hours, timeseparator, minutes, timeseparator , seconds);
01675         }
01676        else  
01677         {
01678           timestr.Format(__L("%02d%c%02d"), hours, timeseparator, minutes, timeseparator);
01679         }
01680     }
01681 
01682   if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHMILLISECONDS))
01683     {
01684       timestr.AddFormat(__L(".%03d"), milliseconds);
01685     }  
01686 
01687   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE))
01688     {
01689       string += datestr;
01690     }
01691 
01692   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME))
01693     {
01694       if(!string.IsEmpty()) 
01695         {
01696           if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_UTC))
01697             {
01698               string += __L("T");
01699             }
01700            else    
01701             {
01702               string += __L(" ");
01703             }
01704         }
01705 
01706       string += timestr;
01707     }
01708 
01709   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_UTC))
01710     {
01711       if(IsModificatorActive(modificator, XDATETIME_FORMAT_ISO8601_ADDHOUROFFSET))
01712         {
01713           int meridiandiff  = GetMeridianDifference();
01714 
01715           if(!meridiandiff)
01716             {
01717               string.Add(__L("Z"));
01718             }
01719            else
01720             {
01721               XDATETIME*  datetime = GEN_XFACTORY.CreateDateTime();
01722               if(datetime)
01723                 {
01724                   XSTRING meridiandiffstr;  
01725                   int     bias         = 0;
01726                   
01727                   datetime->IsDayLigthSavingTime(&bias);
01728                   
01729                   datetime->SetToZero();
01730 
01731                   datetime->SetIsLocal(true);
01732 
01733                   datetime->SetDay(1);
01734                   datetime->SetMonth(1);
01735                   datetime->SetYear(1);
01736 
01737                   datetime->AddMinutes(abs(meridiandiff) + abs(bias));
01738               
01739                   datetime->GetDateTimeToString(XDATETIME_FORMAT_ADDTIME, meridiandiffstr);
01740 
01741                   string.AddFormat(__L("%c%s"), (meridiandiff>0)?__C('+'):__C('-'), meridiandiffstr.Get());
01742 
01743                   GEN_XFACTORY.DeleteDateTime(datetime);
01744                 }
01745             }
01746         }
01747        else
01748         {
01749           string.Add(__L("Z"));
01750         }
01751     }
01752      
01753   return IsValidDate(); 
01754 }
