01769 bool XDATETIME::GetDateTimeFromString(XSTRING& string, XWORD modificator)
01770 {
01771   int           index        = 0;
01772   XVECTOR<int>  typeblock;
01773   int           nblock       = 0;
01774   int           startindex   = 0;
01775   XCHAR         separator    = ((IsModificatorActive(modificator, XDATETIME_FORMAT_DATEWITHDASH))?__C('-'):__C('/'));
01776   XSTRING       validcharacters;
01777 
01778   validcharacters  = __L("0123456789 :.");
01779   validcharacters += separator;
01780 
01781   if(!string.AreValidCharacters(validcharacters.Get())) return false;
01782 
01783   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDAYOFWEEK)) && (IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDAYOFWEEK)))    
01784     {
01785       typeblock.Add(1);
01786     }
01787 
01788   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME)) && (IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTTIME)))         
01789     {
01790       typeblock.Add(2);
01791     }
01792 
01793   if(IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDATE))                                                                                  
01794     {
01795       typeblock.Add(3);
01796     }
01797 
01798   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDTIME)) && (!(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTTIME))))      
01799     {
01800       typeblock.Add(2);
01801     }
01802 
01803   if((IsModificatorActive(modificator, XDATETIME_FORMAT_ADDDAYOFWEEK)) && (!(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDAYOFWEEK)))) 
01804     {
01805       typeblock.Add(1);
01806     }
01807 
01808   while(index<=(int)string.GetSize())
01809     {
01810       XCHAR character = string.Get()[index];
01811 
01812       if((character == 0x20) || (index==(int)string.GetSize()))
01813         {
01814           XSTRING partial;
01815 
01816           if(index<(int)string.GetSize())
01817             {
01818               string.Copy(startindex, index, partial);
01819             }
01820            else 
01821             {
01822               string.Copy(startindex, partial);
01823             }
01824 
01825           if(!partial.IsEmpty())
01826             {
01827               switch(typeblock.Get(nblock))
01828                 {
01829                   case 1: break; // Day of Week not used (can be calculated).
01830 
01831                   case 2: if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHMILLISECONDS))
01832                             {
01833                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01834                                 {
01835                                   partial.UnFormat(__L("%d:%d:%d.%d"), &hours ,&minutes ,&seconds, &milliseconds);
01836                                 }
01837                                else  
01838                                 {
01839                                   partial.UnFormat(__L("%d:%d:%d"), &hours ,&minutes, &milliseconds);
01840                                 }
01841                             }
01842                            else
01843                             {
01844                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_TIMEWITHSECONDS))
01845                                 {
01846                                   partial.UnFormat(__L("%d:%d:%d"), &hours ,&minutes ,&seconds);
01847                                 }
01848                                else  
01849                                 {
01850                                   partial.UnFormat(__L("%d:%d"), &hours ,&minutes);
01851                                 }
01852                             }
01853                           break;
01854 
01855                   case 3: if(separator == __C('-'))
01856                             {
01857                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEDAY)) 
01858                                 {
01859                                   partial.UnFormat(__L("%d-%d-%d"), &day, &month, &year);
01860                                 }
01861                                else 
01862                                 {
01863                                   if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEMONTH))
01864                                     {
01865                                       partial.UnFormat(__L("%d-%d-%d"), &month, &day, &year);
01866                                     }
01867                                    else 
01868                                     {
01869                                       if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEYEAR))
01870                                         {
01871                                           partial.UnFormat(__L("%d-%d-%d"), &year, &month, &day);
01872                                         }
01873                                        else 
01874                                         {
01875                                           partial.UnFormat(__L("%d-%d-%d"), &day, &month, &year);
01876                                         }
01877                                     }
01878                                 }
01879                             }
01880 
01881                           if(separator == __C('/'))
01882                             {
01883                               if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEDAY))
01884                                 {
01885                                   partial.UnFormat(__L("%d/%d/%d"), &day, &month, &year);
01886                                 }
01887                                else 
01888                                 {
01889                                   if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEMONTH))
01890                                     {
01891                                       partial.UnFormat(__L("%d/%d/%d"), &month, &day, &year);
01892                                     }
01893                                    else 
01894                                     {
01895                                       if(IsModificatorActive(modificator, XDATETIME_FORMAT_FIRSTDATEYEAR))
01896                                         {
01897                                           partial.UnFormat(__L("%d/%d/%d"), &year, &month, &day);
01898                                         }
01899                                        else 
01900                                         {
01901                                           partial.UnFormat(__L("%d/%d/%d"), &day, &month, &year);
01902                                         }
01903                                     }
01904                                 }
01905                             }
01906 
01907                           break;
01908                 }
01909 
01910               nblock++;
01911             }
01912 
01913           startindex = index+1;
01914         }
01915 
01916       index++;
01917     }
01918 
01919   typeblock.DeleteAll();
01920 
01921   return IsValidDate();
01922 }
