02141 
02151 int XDATETIME::GetDayOfYear() 
02152 {
02153   int dayspermonth[] = {0, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
02154   int dayofyear = 0;
02155   int i;
02156 
02157   // Check for leap year and update the number of days in February
02158   if((year % 4 == 0 && year % 100 != 0) || (year % 400 == 0)) 
02159     {
02160       dayspermonth[2] = 29;
02161     }
02162 
02163   // Sum the days of the previous months
02164   for(i = 1; i < month; i++) 
02165     {
02166       dayofyear += dayspermonth[i];
02167     }
02168 
02169   // Add the current day
02170   dayofyear += day;
02171 
02172   return dayofyear;
02173 }
02174 
02175 
02185 int XDATETIME::GetWeekOfYear() 
02186 {
02187   int dayofyear   = XDATETIME::GetDayOfYear();
02188   int daysperweek = 7;
02189   int weekofyear  = 0;
02190     
02191   // Calculate the week of the year
02192   weekofyear = (dayofyear - 1) / daysperweek + 1;
02193 
02194   return weekofyear;
02195 }
