02207 XQWORD XDATETIME::GetEPOCHFormat()
02208 {
02209   Read();
02210 
02211   XDATETIME origin;
02212 
02213   origin.SetToZero();
02214   origin.SetDay(1);
02215   origin.SetMonth(1);
02216   origin.SetYear(1970);
02217 
02218   XQWORD seconds_origin = origin.GetSeconsFromDate(false);
02219   XQWORD seconds_actual = GetSeconsFromDate(false);
02220 
02221   return (seconds_actual - seconds_origin);
02222 }
