02325 
02337 int XDATETIME::Compare(XDATETIME& datetime)
02338 {
02339   if(datetime.GetYear() > GetYear()) 
02340     {
02341       return  1;
02342     }
02343 
02344   if(datetime.GetYear() < GetYear()) 
02345     {
02346       return -1;
02347     }
02348 
02349   if(datetime.GetMonth() > GetMonth()) 
02350     {
02351       return  1;
02352     }
02353 
02354   if(datetime.GetMonth() < GetMonth()) 
02355     {
02356       return -1;
02357     }
02358 
02359   if(datetime.GetDay() > GetDay()) 
02360     {
02361       return  1;
02362     }
02363 
02364   if(datetime.GetDay() < GetDay()) 
02365     {
02366       return -1;
02367     }
02368 
02369   if(datetime.GetHours() > GetHours()) 
02370     {
02371       return  1;
02372     }
02373 
02374   if(datetime.GetHours() < GetHours()) 
02375     {
02376       return -1;
02377     }
02378 
02379   if(datetime.GetMinutes() > GetMinutes()) 
02380     {
02381       return  1;
02382     }
02383 
02384   if(datetime.GetMinutes() < GetMinutes()) 
02385     {
02386       return -1;
02387     }
02388 
02389   if(datetime.GetSeconds() > GetSeconds()) 
02390     {
02391       return  1;
02392     }
02393 
02394   if(datetime.GetSeconds() < GetSeconds()) 
02395     {
02396       return -1;
02397     }
02398 
02399   if(datetime.GetMilliSeconds() > GetMilliSeconds()) 
02400     {
02401       return  1;
02402     }
02403 
02404   if(datetime.GetMilliSeconds() < GetMilliSeconds()) 
02405     {
02406       return -1;
02407     }
02408 
02409   return 0;
02410 }
