00091 #define XDATETIME_FORMAT_ADDDATE                            0x00000001                        // Add date in format of string
00092 #define XDATETIME_FORMAT_ADDTIME                            0x00000002                        // Add time in format of string
00093 #define XDATETIME_FORMAT_ADDDAYOFWEEK                       0x00000004                        // Add day of the week + ( "monday", "tuesday", etc...)       [Not applicable in ISO 8901]
00094 #define XDATETIME_FORMAT_FIRSTDATEDAY                       0x00000010                        // First goes the day in the date DD MM YYYY                  [Not applicable in ISO 8901] 
00095 #define XDATETIME_FORMAT_FIRSTDATEMONTH                     0x00000020                        // First goes the month in the date MM DD YYYY                [No applicable in ISO 8901] 
00096 #define XDATETIME_FORMAT_FIRSTDATEYEAR                      0x00000040                        // First goes the year in the date YYYY MM DD                 [Always applicable in ISO 8901] 
00097 #define XDATETIME_FORMAT_FIRSTTIME                          0x00000080                        // First goes the time and then the date                      [Not applicable in ISO 8901] 
00098 #define XDATETIME_FORMAT_FIRSTDAYOFWEEK                     0x00000100                        // First goes the day of week                                 [Not applicable in ISO 8901] 
00099 #define XDATETIME_FORMAT_TIMEWITHSECONDS                    0x00000200                        // Add Seconds to time 
00100 #define XDATETIME_FORMAT_TIMEWITHMILLISECONDS               0x00000400                        // Add Milliseconds to time   
00101 #define XDATETIME_FORMAT_DATEWITHDASH                       0x00000800                        // Date separated by Dash                                     [Always applicable in ISO 8901]                            
00102 #define XDATETIME_FORMAT_TEXTMONTH                          0x00001000                        // Month in text (not numeric) : January, February, etc...    [Not applicable in ISO 8901]
00103 #define XDATETIME_FORMAT_ISO8601_BASIC                      0x00002000                        // Basic Representation
00104 #define XDATETIME_FORMAT_ISO8601_UTC                        0x00004000                        // UTC Representation
00105 #define XDATETIME_FORMAT_ISO8601_ADDHOUROFFSET              0x00008000                        // Add Hour Offset 
00106 
00107 #define XDATETIME_FORMAT_STANDARD                           (XDATETIME_FORMAT_ADDDATE | XDATETIME_FORMAT_FIRSTDATEDAY  | XDATETIME_FORMAT_ADDTIME         | XDATETIME_FORMAT_TIMEWITHSECONDS)
00108 #define XDATETIME_FORMAT_POSTGRESQL                         (XDATETIME_FORMAT_ADDDATE | XDATETIME_FORMAT_FIRSTDATEYEAR | XDATETIME_FORMAT_ADDTIME         | XDATETIME_FORMAT_TIMEWITHSECONDS)
00109 #define XDATETIME_FORMAT_ISO8601_STANDARD                   (XDATETIME_FORMAT_ADDDATE | XDATETIME_FORMAT_ADDTIME       | XDATETIME_FORMAT_TIMEWITHSECONDS)
00110 #define XDATETIME_FORMAT_DMY                                (XDATETIME_FORMAT_ADDDATE | XDATETIME_FORMAT_FIRSTDATEDAY)
00111 #define XDATETIME_FORMAT_YMD                                (XDATETIME_FORMAT_ADDDATE | XDATETIME_FORMAT_FIRSTDATEYEAR)
00112 #define XDATETIME_FORMAT_MDY                                (XDATETIME_FORMAT_ADDDATE | XDATETIME_FORMAT_FIRSTDATEMONTH)
00113 
00114 #define XDATETIME_FORMAT_TIMEONLY                           (XDATETIME_FORMAT_ADDTIME | XDATETIME_FORMAT_TIMEWITHSECONDS)
