00116 #define XDATETIME_SECONDSINYEAR                             31556926
00117 #define XDATETIME_SECONDSINMONTH                            2629743   //2629743.83
00118 #define XDATETIME_SECONDSINDAY                              86400
00119 #define XDATETIME_SECONDSINHOUR                             3600
00120 
00121 #define XDATETIME_SECONDSYEARS(allseconds)                  (XDWORD)(allseconds / XDATETIME_SECONDSINYEAR)
00122 #define XDATETIME_SECONDSMONTHS(allseconds)                 (XDWORD)(allseconds % XDATETIME_SECONDSINYEAR) / XDATETIME_SECONDSINMONTH
00123 #define XDATETIME_SECONDSDAYS(allseconds)                   (XDWORD)((allseconds % XDATETIME_SECONDSINYEAR) % XDATETIME_SECONDSINMONTH) / XDATETIME_SECONDSINDAY
00124 #define XDATETIME_SECONDSHOURS(allseconds)                  (XDWORD)(((allseconds % XDATETIME_SECONDSINYEAR) % XDATETIME_SECONDSINMONTH) % XDATETIME_SECONDSINDAY) / XDATETIME_SECONDSINHOUR
00125 #define XDATETIME_SECONDSMINUTES(allseconds)                (XDWORD)((((allseconds % XDATETIME_SECONDSINYEAR) % XDATETIME_SECONDSINMONTH) % XDATETIME_SECONDSINDAY) % XDATETIME_SECONDSINHOUR) / 60
00126 #define XDATETIME_SECONDSSECONDS(allseconds)                (XDWORD)((((allseconds % XDATETIME_SECONDSINYEAR) % XDATETIME_SECONDSINMONTH) % XDATETIME_SECONDSINDAY) % XDATETIME_SECONDSINHOUR) % 60
