00643 bool XDRIVEIMAGEMANAGER::ReadToIMGFile(XDRIVEIMAGEMANAGER_DRIVE& drive, XPATH& pathfileIMG, XQWORD nsectorsperblock)
00644 {
00645   XFILE*   xfileIMG;
00646   XBUFFER  readbuffer;
00647   XQWORD   nsectorstoread = nsectorsperblock;
00648   XQWORD   nsectorsread   = 0;
00649   bool     status         = 0;
00650 
00651   if(!drive.GetSize()) return status;
00652 
00653   GEN_XFACTORY_CREATE(xfileIMG, Create_File())
00654   if(xfileIMG)
00655     {
00656       if(xfileIMG->Create(pathfileIMG))
00657         {
00658           XQWORD indexsector=0;
00659 
00660           while(indexsector<drive.GetNumberSectors())
00661             {
00662               if((indexsector + nsectorstoread) > drive.GetNumberSectors()) nsectorstoread = (drive.GetNumberSectors() - indexsector);
00663 
00664               XDRIVEIMAGEMANAGER_XEVENT xevent(this, XDRIVEIMAGEMANAGER_XEVENT_TYPE_READTOIMGFILE);
00665 
00666               xevent.SetDrive(&drive);
00667               xevent.SetIndexSector(indexsector);
00668               xevent.SetNumberSectorsInOperation(nsectorstoread);
00669               xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_NONE);
00670 
00671               status = Read(drive, indexsector, nsectorstoread, readbuffer);
00672               if(!status) xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_READSECTORS);
00673 
00674               if(status)
00675                 {
00676                   status = xfileIMG->Write(readbuffer);
00677                   if(!status) xevent.SetError(XDRIVEIMAGEMANAGER_ERROR_WRITEIMGFILE);
00678                 }
00679 
00680               if(status) nsectorsread += nsectorstoread;
00681 
00682               lastoperationpercent = (((float)nsectorsread*100)/(float)drive.GetNumberSectors());
00683 
00684               xevent.SetError(lasterror);
00685               xevent.SetOperationPercent(lastoperationpercent);
00686 
00687               PostEvent(&xevent);
00688 
00689               if(!status) break;
00690 
00691               indexsector += nsectorstoread;
00692             }
00693 
00694           xfileIMG->Close();
