00332 bool XFILE::EraseBlock(XQWORD size)
00333 {
00334   XBYTE* tbuffer;
00335   XQWORD position;
00336   XQWORD sizetomove;
00337   XDWORD nblocks;
00338   XDWORD rest;
00339   bool   status = true;
00340 
00341   if(!isopen) return false;
00342 
00343   if(!GetPosition(position)) return false;
00344 
00345   sizetomove = GetSize() - position - size;
00346 
00347   tbuffer= new XBYTE [XFILE_MAXBUFFER];
00348   if(!tbuffer) return false;
00349 
00350   nblocks = (XDWORD)(sizetomove / XFILE_MAXBUFFER);
00351   rest    = (XDWORD)(sizetomove % XFILE_MAXBUFFER);
00352 
00353   for(XDWORD c=0; c<nblocks; c++)
00354     {
00355       SetPosition(position + size + ((XQWORD)c * XFILE_MAXBUFFER));
00356       if(Read(tbuffer, XFILE_MAXBUFFER))
00357         {
00358           SetPosition(position + ((XQWORD)c * XFILE_MAXBUFFER));
00359           Write(tbuffer, XFILE_MAXBUFFER);
00360         }
00361     }
00362 
00363   SetPosition(position + size +((XQWORD)nblocks * XFILE_MAXBUFFER));
00364   if(Read(tbuffer, rest))
00365     {
00366       SetPosition(position + ((XQWORD)nblocks * XFILE_MAXBUFFER));
00367       Write(tbuffer, rest);
00368     }
00369 
00370   SetSize(GetSize() - size);
00371 
00372   delete [] tbuffer;
00373 
00374   return status;
00375 }
