00924 XVARIANT* XFILECFG::GetValue(XCHAR* group, XCHAR* ID)
00925 {
00926   XVARIANT* value = NULL;
00927 
00928   if((!group) || (!ID))
00929     {
00930       return NULL;
00931     }
00932 
00933   for(XDWORD c=0; c<values.GetSize(); c++)
00934     {
00935       XFILECFGVALUE* cfgvalue = values.Get(c);
00936       if(cfgvalue)
00937         {
00938           if((!cfgvalue->GetGroup()->Compare(group, true)) && (!cfgvalue->GetID()->Compare(ID, true)))
00939             {
00940               value = new XVARIANT();
00941               if(value)
00942                 {
00943                   switch(cfgvalue->GetType())
00944                     {
00945                       case XFILECFG_VALUETYPE_UNKNOWN :
00946                                              default  : delete value; 
00947                                                         value = NULL;
00948                                                         break;
00949 
00950                       case XFILECFG_VALUETYPE_INT     : (*value) = (*(int*)(cfgvalue->GetValue()));     break; 
00951                       case XFILECFG_VALUETYPE_MASK    : (*value) = (*(XWORD*)(cfgvalue->GetValue()));   break;
00952                       case XFILECFG_VALUETYPE_FLOAT   : (*value) = (*(float*)(cfgvalue->GetValue()));   break;                                                        
00953 
00954                       case XFILECFG_VALUETYPE_STRING  : { XSTRING* _value = (XSTRING*)(cfgvalue->GetValue());                                                        
00955                                                           (*value) = _value->Get(); 
00956                                                         }
00957                                                         break;
00958 
00959                       case XFILECFG_VALUETYPE_BOOLEAN : (*value) = (*(bool*)(cfgvalue->GetValue()));    break; 
00960                     }
00961                 }    
00962             }
00963         }
00964     }
00965   
00966   return value;
00967 }
