01405 bool XFILECFG::GenerateKeySecuence(XCHAR* IDbase, XCHAR* mask, int index, XSTRING& key)
01406 {
01407   XSTRING keymask;      
01408   
01409   key.Empty();     
01410   
01411   if(!IDbase) return false;
01412     
01413   if(mask)  
01414     {
01415       keymask.Format(__L("%s%s"), IDbase, mask);
01416       key.Format(keymask.Get(), index);
01417 
01418     } else key.Format(__L("%s%d"), IDbase, index);
01419   
01420   return true;
01421 }
