00759 bool XFILECSV::CreateRecordOfLine(XSTRING& line, XFILECSV_RECORD* record)
00760 {
00761   if(line.IsEmpty()) return false;
00762 
00763   int start = 0;
00764   int end   = 0;
00765 
00766   do{ end = line.Find(__L(";"),true,start);
00767       if(end == XSTRING_NOTFOUND)
00768         {
00769           end = line.Find(__L(","),true,start);
00770           if(end != XSTRING_NOTFOUND) SetSeparator(__C(','));
00771 
00772         } else SetSeparator(__C(';'));
00773 
00774       XSTRING element;
00775 
00776       if(end != XSTRING_NOTFOUND)
00777         {
00778           line.Copy(start,end,element);
00779 
00780           record->AddElement(element);
00781 
00782           start = end+1;
00783         }
00784        else
00785         {
00786           line.Copy(start,element);
00787           record->AddElement(element);
00788 
00789           break;
00790         }
00791 
00792     } while(1);
00793 
00794   return true;
00795 }
