00157 bool XFILECOBOL_PIC::ResolveMask()
00158 {
00159   if(mask.IsEmpty())
00160     {
00161       XTRACE_PRINTCOLOR(4, __L("Cobol Reader: Error PIC Missing"));
00162       return false;
00163     }
00164 
00165   hassign    = false;
00166   ndecimals  = 0;
00167   sizedata   = 0;
00168 
00169   XDWORD index      = 0;
00170 
00171   while(index < mask.GetSize())
00172     {
00173       switch(mask.Get()[index])
00174         {
00175           case __C('X')   : type = XFILECOBOL_PIC_TYPE_BINARY;
00176                             maskdecondensed += __C('X');
00177                             sizedata++;
00178                             index++;
00179                             break;
00180 
00181           case __C('A')   : type = XFILECOBOL_PIC_TYPE_ALPHA;
00182                             maskdecondensed += __C('A');
00183                             sizedata++;
00184                             index++;
00185                             break;
00186 
00187           case __C('9')   : if(type != XFILECOBOL_PIC_TYPE_DECIMAL) type = XFILECOBOL_PIC_TYPE_NUMERIC;
00188                             if(type == XFILECOBOL_PIC_TYPE_DECIMAL) ndecimals++;
00189                             maskdecondensed += __C('9');
00190                             sizedata++;
00191                             index++;
00192                             break;
00193 
00194           case __C(',')   : type = XFILECOBOL_PIC_TYPE_DECIMAL;
00195                             maskdecondensed += __C(',');
00196                             sizedata++;
00197                             index++;
00198                             break;
00199 
00200           case __C('S')   : hassign = true;
00201                             maskdecondensed += __C('S');
00202                             index++;
00203                             break;
00204 
00205           case __C('(')   : { XSTRING modificator;
00206                               int     sizemodificator;
00207 
00208                               int end = mask.Find(__L(")"), true, index+1);
00209                               if(end == XSTRING_NOTFOUND)
00210                                 {
00211                                   XTRACE_PRINTCOLOR(4, __L("Cobol Reader: Error PIC invalid %s. Missing ')'"), mask.Get());
00212                                   return false;
00213                                 }
00214 
00215                              mask.Copy(index, end+1, modificator);
00216                              modificator.UnFormat(__L("(%d)"), &sizemodificator);
00217 
00218                              if(!sizemodificator)
00219                                {
00220                                  XTRACE_PRINTCOLOR(4, __L("Cobol Reader: Error PIC invalid %s. Modificador is zero '(0)'"), mask.Get());
00221                                  return false;
00222                                }
00223 
00224                              for(int c=0; c<sizemodificator-1; c++)
00225                                 {
00226                                   maskdecondensed += mask.Get()[index-1];
00227                                   sizedata++;
00228                                   if(type == XFILECOBOL_PIC_TYPE_DECIMAL) ndecimals++;
00229                                 }
00230 
00231                              index = end;
00232                            }
00233                            break;
00234 
00235           case __C(')')  : index++;
00236                            break;
00237 
00238           case __C('V')  : type = XFILECOBOL_PIC_TYPE_DECIMAL;
00239                            maskdecondensed += __C('V');
00240                            index++;
00241                            break;
00242 
00243                  default : XTRACE_PRINTCOLOR(4, __L("Cobol Reader: Error PIC invalid %s. Unknown Specifier '%c'"), mask.Get(), mask.Get()[index]);
00244                            return false;
00245                            break;
00246         }
00247     }
00248 
00249    switch(computationaltype)
00250      {
00251         case 1  : // Float point 4 Bytes
00252                   break;
00253 
00254         case 2  : // Float Point 8 Bytes
00255                   break;
00256 
00257         case 3  : // BCD + Sing
00258                   sizedata = ((sizedata + 1) / 2) + (((sizedata + 1) % 2)?1:0);
00259                   break;
00260 
00261         case 4  : break;
00262 
00263         case 5  : // Conversion Packet number standard binary data
00264                   if((sizedata >= 1) && (sizedata <= 2))
00265                     {
00266                       sizedata = 1;
00267                     }
00268                    else
00269                     {
00270                       if((sizedata >=3) && (sizedata <= 4))
00271                         {
00272                           sizedata = 2;
00273                         }
00274                        else
00275                         {
00276                           if((sizedata >= 5) && (sizedata <= 9))
00277                             {
00278                               sizedata = 4;
00279                             }
00280                            else
00281                             {
00282                               if((sizedata >= 10) && (sizedata <= 18))
00283                                 {
00284                                   sizedata = 8;
00285                                 }
00286                                else
00287                                 {
00288                                   XTRACE_PRINTCOLOR(4, __L("Cobol Reader: Error PIC invalid %s. Size of modificator to long %s > 18 "), mask.Get(), sizedata);
00289                                   return false;
00290                                 }
00291                             }
00292                         }
00293                     }
00294                   break;
00295      }
00296 
00297   return true;
00298 }
