00778 XFILECOBOL_FIELD* XFILECOBOL_RECORD::GetField(int index)
00779 {
00780   if(!handle)               return NULL;
00781   if(!handle->GetFDTree())  return NULL;
00782 
00783   XFILECOBOL_FD_TREENODEITERATOR  it(handle->GetFDTree()->Begin());
00784   XFILECOBOL_FD_TREENODEITERATOR  it_end(handle->GetFDTree()->End());
00785 
00786   int counterindex = 0;
00787 
00788   while(it != it_end)
00789     {
00790       XFILECOBOL_FIELD* field  = (*it);
00791       if(!field) break;
00792 
00793       if(counterindex >= index) return field;
00794 
00795       counterindex++;
00796       ++it;
00797     }
00798 
00799   return NULL;
00800 }
