00814 XFILECOBOL_FIELD* XFILECOBOL_RECORD::GetField(XCHAR* name)
00815 {
00816   if(!handle) return NULL;
00817 
00818   XFILECOBOL_FD_TREENODEITERATOR it(handle->GetFDTree()->Begin());
00819   XFILECOBOL_FD_TREENODEITERATOR it_end(handle->GetFDTree()->End());
00820 
00821   while(it != it_end)
00822     {
00823       XFILECOBOL_FIELD* field = (*it);
00824       if(!field) break;
00825 
00826       if(!field->GetName()->Compare(name, true)) return field;
00827 
00828       ++it;
00829     }
00830 
00831   return NULL;
00832 }
