00913 bool XFILECOBOL_RECORD::GetFieldData(XFILECOBOL_FD_TREENODEITERATOR& it, XVARIANT& variant)
00914 {
00915   XBUFFER           data;
00916   XFILECOBOL_FIELD* field = GetField(it);
00917 
00918   if(!field) return false;
00919 
00920   if(!GetFieldData(it, data)) return false;
00921 
00922   bool status = false;
00923   if(data.GetSize()) status = ConvertDataTo(field->GetPIC(), data, variant);
