01064 bool XFILECOBOL_RECORD::GetFieldData(XFILECOBOL_FD_TREENODEITERATOR& it, XBUFFER& data)
01065 {
01066   if(!handle)               return false;
01067   if(!handle->GetFDTree())  return false;
01068 
01069   XFILECOBOL_FIELD* field = (*it);
01070   if(!field)                           return false;
01071   if(!field->GetPIC())                 return false;
01072   if(!field->GetPIC()->GetSizeData())  return false;
01073 
01074   int position = handle->CalculatePositionData(it);
01075   if(position == -1) return false;
01076 
01077   data.Resize(field->GetPIC()->GetSizeData());
01078   this->data.Get(data.Get(), data.GetSize(), position);
01079 
01080   return true;
01081 }
