01134 bool XFILECOBOL_RECORD::ConvertDataTo(XFILECOBOL_PIC* picture, XBUFFER& bufferdata, XSTRING& datastring)
01135 {
01136   datastring.Empty();
01137 
01138   if(!picture)              return false;
01139   if(!picture->GetMask())   return false;
01140   if(!bufferdata.GetSize()) return false;
01141 
01142   XSTRING mask;
01143 
01144   mask = picture->GetMaskDecondensed()->Get();
01145   if(!mask.GetSize()) return false;
01146 
01147   int indexdata = 0;
01148 
01149   switch(picture->GetComputationalType())
01150     {
01151       case  0 : for(XDWORD c=0; c<mask.GetSize(); c++)
01152                   {
01153                     switch(mask.Get()[c])
01154                       {
01155                         case __C('S') : { XBYTE lastdata = bufferdata.GetLastByte();
01156                                           if(lastdata & XFILECOBOL_MASK_SIGN)
01157                                             {
01158                                               datastring += __L("-");
01159                                               lastdata &= ~XFILECOBOL_MASK_SIGN;
01160 
01161                                               bufferdata.Get()[bufferdata.GetSize()-1] = lastdata;
01162                                             }
01163                                         }
01164                                         break;
01165 
01166                         case __C('9') :
01167                         case __C('X') :
01168                         case __C('A') : datastring.AddFormat(__L("%c"),  bufferdata.Get()[indexdata]);
01169                                         indexdata++;
01170                                         break;
01171 
01172                         case __C('V') :
01173                         case __C('.') : datastring += __L(".");
01174                                         break;
01175                       }
01176                   }
01177                 break;
01178 
01179       case  1 : break;
01180 
01181       case  3 : { XBYTE lastdata = bufferdata.GetLastByte();
01182                   lastdata &= 0x0F;
01183 
01184                   switch(lastdata)
01185                     {
01186                       case 0x0C : break;
01187                       case 0x0D : datastring = __L("-");
01188                                   break;
01189                       case 0x0F : break;
01190                     }
01191 
01192                   lastdata>>=0x4;
01193 
01194                   for(XDWORD c=0; c< bufferdata.GetSize()-1; c++)
01195                     {
01196                       datastring.AddFormat(__L("%02X"), bufferdata.Get()[c]);
01197                     }
01198 
01199                   datastring.AddFormat(__L("%1X"), lastdata);
01200                 }
01201                 break;
01202 
01203       case  5 : switch(bufferdata.GetSize())
01204                   {
01205                     case 1 : { char byte;
01206 
01207                                bufferdata.Get((XBYTE&)byte);
01208                                datastring.Format(__L("%02d"), byte);
01209                              }
01210                              break;
01211 
01212                     case 2 : { short word;
01213 
01214                                bufferdata.Get((XWORD&)word);
01215                                datastring.Format(__L("%04d"), word);
01216                              }
01217                              break;
01218 
01219                     case 4 : { int integer;
01220 
01221                                bufferdata.Get((XDWORD&)integer);
01222                                datastring.Format(__L("%08d"), integer);
01223                              }
01224                              break;
01225 
01226                     case 8 : { long long large;
01227 
01228                                bufferdata.Get((XQWORD&)large);
01229                                datastring.Format(__L("%016d"), large);
01230                              }
01231                              break;
01232                   }
01233                 break;
01234 
01235     }
01236 
01237   return true;
01238 }
