01368 int XFILECOBOL_HANDLE::GetIndexNodeForName(XFILECOBOL_FD_TREENODE* node, XCHAR* name)
01369 {
01370   if(!node) return NOTFOUND;
01371 
01372   int index = NOTFOUND;
01373 
01374   for(XDWORD c=0; c<node->GetChildren()->GetSize(); c++)
01375     {
01376       XFILECOBOL_FD_TREENODE* subnode = node->GetChildren()->Get(c);
01377       if(subnode)
01378         {
01379           XFILECOBOL_FIELD* field = subnode->GetData();
01380           if(field)
01381             {
01382               if(!field->GetName()->Compare(name)) return (int)c;
01383             }
01384         }
01385     }
01386 
01387   return index;
01388 }
