01404 
01405 
01417 void XFILECOBOL_HANDLE::SetTypeRecord(XFILECOBOL_RECORD_TYPE type)
01418 {
01419   typerecord = type;
01420 }
01421 
01422 
01435 bool XFILECOBOL_HANDLE::GetInterator(XDWORD index, XFILECOBOL_FD_TREENODEITERATOR& it)
01436 {
01437   if(!GetFDTree()) return false;
01438 
01439   XFILECOBOL_FD_TREENODEITERATOR  it_start(GetFDTree()->Begin());
01440   XFILECOBOL_FD_TREENODEITERATOR  it_end(GetFDTree()->End());
01441 
01442   XDWORD counterindex = 0;
01443 
01444   it = it_start;
01445 
01446   while(it_start != it_end)
01447     {
01448       if(counterindex >= index)
01449         {
01450           it = it_start;
01451           return true;
01452         }
01453 
01454       counterindex++;
01455 
01456       ++it_start;
01457     }
01458 
01459   return false;
01460 }
01461 
01462 
01475 bool XFILECOBOL_HANDLE::GetInterator(XCHAR* name, XFILECOBOL_FD_TREENODEITERATOR& it)
01476 {
01477   if(!GetFDTree()) return false;
01478 
01479   XFILECOBOL_FD_TREENODEITERATOR  it_start(GetFDTree()->Begin());
01480   XFILECOBOL_FD_TREENODEITERATOR  it_end(GetFDTree()->End());
01481 
01482   it = it_start;
01483 
01484   while(it_start != it_end)
01485     {
01486       XFILECOBOL_FIELD* field = (*it_start);
01487       if(!field) return false;
01488 
01489       if(!field->GetName()->Compare(name, true))
01490         {
01491           it = it_start;
01492           return true;
01493         }
01494 
01495       ++it_start;
01496     }
01497 
01498   return false;
01499 }
01500 
01501 
01513 XDWORD XFILECOBOL_HANDLE::CalculateDataSizeNode(XFILECOBOL_FD_TREENODE* node)
01514 {
01515   static XDWORD redefineoffset = 0;
01516   XDWORD size     = 0;
01517   if(!node) return size;
01518 
01519   if(node->GetData()->IsRedefine())
01520     {
01521       node->GetData()->SetRedefineOffset(redefineoffset);
01522 
01523       redefineoffset  = node->GetData()->GetPIC()->GetSizeData();
01524 
01525       for(XDWORD c=0; c<(XDWORD)node->GetNChildren(); c++)
01526         {
01527           XFILECOBOL_FD_TREENODE* subnode = node->GetChild(c);
01528           if(subnode)
01529             {
01530               subnode->GetData()->SetIsRedefine(true);
01531               CalculateDataSizeNode(subnode);
01532             }
01533         }
01534     }
01535    else
01536     {
01537       redefineoffset = 0;
01538 
01539       if(node->GetData()->GetPIC())
01540         {
01541           size = node->GetData()->GetPIC()->GetSizeData();
01542         }
01543 
01544       for(XDWORD c=0; c<(XDWORD)node->GetNChildren(); c++)
01545         {
01546           XFILECOBOL_FD_TREENODE* subnode = node->GetChild(c);
01547           if(subnode)
01548             {
01549               size +=  CalculateDataSizeNode(subnode);
01550             }
01551         }
01552     }
01553 
01554   return size;
01555 }
01556 
01557 
01567 XDWORD XFILECOBOL_HANDLE::CalculateDataSize()
01568 {
01569   XDWORD size = 0;
01570 
01571   XFILECOBOL_FD_TREENODE* node = GetFDTree()->GetRoot();
01572   if(!node)  return size;
01573 
01574   for(XDWORD c=0; c<(XDWORD)node->GetNChildren(); c++)
01575     {
01576       XFILECOBOL_FD_TREENODE* subnode = node->GetChild(c);
01577       if(subnode) size =  CalculateDataSizeNode(subnode);
01578     }
01579 
01580   return size;
01581 }
01582 
01583 
01595 int XFILECOBOL_HANDLE::CalculatePositionData(XFILECOBOL_FD_TREENODEITERATOR& it)
01596 {
01597   if(!GetFDTree()) return -1;
01598 
01599   XFILECOBOL_FD_TREENODEITERATOR  it_start(GetFDTree()->Begin());
01600   XFILECOBOL_FD_TREENODEITERATOR  it_end(GetFDTree()->End());
01601 
01602   int        position            = 0;
01603   static int positionposredefine = 0;
01604 
01605   while(it_start != it_end)
01606     {
01607       XFILECOBOL_FIELD* fieldtmp  = (*it_start);
01608       if(!fieldtmp) break;
01609 
01610       if(fieldtmp)
01611         {
01612           if(fieldtmp->GetPIC())
01613             {
01614               if(!fieldtmp->IsRedefine())
01615                 {
01616                   if(positionposredefine)
01617                     {
01618                       position            = positionposredefine;
01619                       positionposredefine = 0;
01620                     }
01621 
01622                   if(it_start == it) break;
01623 
01624                   position += fieldtmp->GetPIC()->GetSizeData();
01625                 }
01626                else
01627                 {
01628                   if(fieldtmp->GetRedefineName()->IsEmpty())
01629                     {
01630                       position += fieldtmp->GetRedefineOffset();
01631 
01632                       if(it_start == it) break;
01633                     }
01634                    else
01635                     {
01636                       XFILECOBOL_FD_TREENODEITERATOR itredefine(GetFDTree()->Begin());
01637 
01638                       if(GetInterator(fieldtmp->GetRedefineName()->Get(), itredefine))
01639                         {
01640                           position            = CalculatePositionData(itredefine);
01641                           positionposredefine = position + (*itredefine)->GetPIC()->GetSizeData();
01642                         }
01643 
01644                       if(it_start == it) break;
01645                     }
01646                 }
01647             }
01648         }
01649 
01650       ++it_start;
01651     }
01652 
01653   return position;
01654 }
01655 
01656 
01668 XFILECOBOL_FIELD* XFILECOBOL_HANDLE::IsFieldRedefinedByAnother(XCHAR* name)
01669 {
01670   if(!GetFDTree()) return NULL;
01671 
01672   XFILECOBOL_FD_TREENODEITERATOR  it_start(GetFDTree()->Begin());
01673   XFILECOBOL_FD_TREENODEITERATOR  it_end(GetFDTree()->End());
01674 
01675   while(it_start != it_end)
01676     {
01677       XFILECOBOL_FIELD* fieldtmp  = (*it_start);
01678       if(!fieldtmp) break;
01679 
01680       if(!fieldtmp->GetRedefineName()->IsEmpty())
01681         {
01682           if(!fieldtmp->GetRedefineName()->Compare(name))
01683             {
01684               return fieldtmp;
01685             }
01686         }
01687 
01688       ++it_start;
01689     }
01690 
01691   return NULL;
01692 }
