01755 
01770 bool XFILECOBOL::Open(int nfiles, XCHAR* xpathcpyfiles, XCHAR* xpathdatfiles, ...)
01771 {
01772   XPATH     xpath_cpy;
01773   va_list   arg;
01774 
01775   this->xpathcpyfiles      = xpathcpyfiles;
01776   this->xpathcpyfiles.Slash_Add();
01777 
01778   this->xpathdatfiles      = xpathdatfiles;
01779   this->xpathdatfiles.Slash_Add();
01780 
01781   if(capabilitiesfilemap.IsEmpty())
01782     {
01783       XPATH  xpathcapabilitiesfile;
01784 
01785       xpathcapabilitiesfile  = xpathcpyfiles;
01786       xpathcapabilitiesfile.Slash_Add();
01787       xpathcapabilitiesfile += XFILECOBOL_CAPABILITIESFILE;
01788 
01789       CapabilitiesFile_Load(xpathcapabilitiesfile);
01790     }
01791 
01792   va_start(arg, xpathdatfiles);
01793 
01794   handle.GetFDTree()->DeleteContents();
01795   handle.GetFDTree()->DeleteAll();
01796 
01797   for(int c=0; c<nfiles; c++)
01798     {
01799       XCHAR* namefile = va_arg(arg, XCHAR*);
01800 
01801       xpath_cpy = xpathcpyfiles;
01802       xpath_cpy.Slash_Add();
01803       xpath_cpy += namefile;
01804 
01805       XFILETXT*  file_fd = new XFILETXT();
01806       if(file_fd)
01807         {
01808           if(file_fd->Open(xpath_cpy, true))
01809             {
01810               file_fd->ReadAllFile();
01811 
01812               TraslateTextToHandle(file_fd->GetLines(), handle);
01813 
01814               file_fd->Close();
01815             }
01816 
01817           delete file_fd;
01818         }
01819     }
01820 
01821   va_end(arg);
01822 
01823   sizedatarecord = handle.CalculateDataSize();
01824   if(!sizedatarecord) return false;
01825 
01826   if(this->xpathdatafile.IsEmpty()) return false;
01827 
01828 
01829   XPATH xpathnamefile;
01830 
01831   this->xpathdatafile.GetNamefileExt(xpathnamefile);
01832   xpathnamefile.Insert(this->xpathdatfiles.Get(), 0);
01833 
01834 
01835   GEN_XFACTORY_CREATE(xfiledata, Create_File())
01836   if(!xfiledata) return false;
01837 
01838   bool status = xfiledata->Open(xpathnamefile.Get());
01839 
01840   if(status)
01841     {
01842       xfiledata->SetPosition(0);
01843       if(xfiledata->Read((XBYTE*)&fileheadersize, sizeof(XDWORD)))
01844         {
01845           if(GEN_XSYSTEM.HardwareUseLittleEndian()) fileheadersize = SWAPDWORD(fileheadersize);
01846 
01847           switch(fileheadersize)
01848             {
01849               case 0x307E0000 : { fileheadersize = 0x80;
01850                                   SetHaveFileHeaders(true);
01851                                   type = XFILECOBOL_FILE_TYPE_MF4;
01852                                 }
01853                                 break;
01854 
01855               case 0x3000007C : { fileheadersize = 0x80;
01856                                   SetHaveFileHeaders(true);
01857                                   type = XFILECOBOL_FILE_TYPE_MF4;
01858                                 }
01859                                 break;
01860 
01861               case 0x300003FC : { fileheadersize = 0x80;
01862                                   SetHaveFileHeaders(true);
01863                                   type = XFILECOBOL_FILE_TYPE_C_ISAM;
01864                                 }
01865                                 break;
01866 
01867                     default   : { fileheadersize = 0x00;
01868                                   SetHaveFileHeaders(false);
01869                                   type = XFILECOBOL_FILE_TYPE_C_ISAM;
01870                                 }
01871                                 break;
01872             }
01873 
01874           if(fileheadersize)
01875             {
01876               XWORD ID = 0;
01877 
01878               status = false;
01879 
01880               xfiledata->SetPosition(36);
01881               if(xfiledata->Read((XBYTE*)&ID, sizeof(XWORD)))
01882                 {
01883                   if(GEN_XSYSTEM.HardwareUseLittleEndian()) ID = SWAPWORD(ID);
01884                   if(ID == 0x003E)
01885                     {
01886                       xfiledata->SetPosition(54);
01887                       if(xfiledata->Read((XBYTE*)&maxfilerecordsize, sizeof(XDWORD)))
01888                         {
01889                           if(GEN_XSYSTEM.HardwareUseLittleEndian()) maxfilerecordsize = SWAPDWORD(maxfilerecordsize);
01890 
01891                           xfiledata->SetPosition(58);
01892                           if(xfiledata->Read((XBYTE*)&minfilerecordsize, sizeof(XDWORD)))
01893                             {
01894                               if(GEN_XSYSTEM.HardwareUseLittleEndian()) minfilerecordsize = SWAPDWORD(minfilerecordsize);
01895 
01896                               if(maxfilerecordsize < 4095) fileheaderrecordize = 2; else fileheaderrecordize = 4;
01897 
01898                               // Check if the size in the file header is diferent
01899                               if(maxfilerecordsize == handle.CalculateDataSize())
01900                                 {
01901                                   status = true;
01902                                 }
01903 
01904                               if(status) filerecordpadding = 16 - ((fileheaderrecordize + maxfilerecordsize) % 16);
01905                             }
01906                         }
01907                     }
01908                 }
01909             }
01910         }
01911     }
01912 
01913   return status;
01914 }
01915 
