01996 
02006 XDWORD XFILECOBOL::GetNRecords()
02007 {
02008   XQWORD size       = xfiledata->GetSize();
02009   XDWORD sizerecord = handle.CalculateDataSize();
02010 
02011   if(havefileheaders)
02012     {
02013       sizerecord += (filerecordpadding + fileheaderrecordize);
02014       size       -= fileheadersize;
02015     }
02016 
02017   if(!size || !sizerecord) return 0;
02018 
02019   return (XDWORD)(size / sizerecord);
02020 }
02021 
02022 
02035 bool XFILECOBOL::ReadRecord(XDWORD index, XFILECOBOL_RECORD& record)
02036 {
02037   if(!IsOpen()) return false;
02038 
02039   if(index<1) return false;
02040 
02041   int position = 0;
02042 
02043   if(havefileheaders)  position = fileheadersize;
02044 
02045   switch(record.GetType())
02046     {
02047       case XFILECOBOL_RECORD_TYPE_UNKNOWN           :
02048                                         default     :
02049 
02050       case XFILECOBOL_RECORD_TYPE_INDEXED           :
02051       case XFILECOBOL_RECORD_TYPE_LINE_SEQUENTIAL   :  if(havefileheaders)
02052                                                           {
02053                                                               position += ((index-1) * (sizedatarecord + fileheaderrecordize + filerecordpadding));
02054                                                               position += fileheaderrecordize;
02055                                                           }
02056                                                          else position += ((index-1) * (sizedatarecord + 1));
02057 
02058                                                       break;
02059       case XFILECOBOL_RECORD_TYPE_SEQUENTIAL        :
02060       case XFILECOBOL_RECORD_TYPE_RELATIVE          : break;
02061     }
02062 
02063 
02064 
02065   xfiledata->SetPosition((XDWORD)position);
02066 
02067   return xfiledata->Read(record.GetData()->Get(), record.GetData()->GetSize());
02068 }
02069 
