02105 
02106   if(xfiledata)
02107     {
02108       xfiledata->Close();
02109 
02110       GEN_XFACTORY.Delete_File(xfiledata);
02111       xfiledata = NULL;
02112     }
02113 
02114   return true;
02115 }
