02147 
02159 bool XFILECOBOL::CapabilitiesFile_Load(XPATH& xpathcapabilitiesfile)
02160 {
02161   XFILETXT* filetxt;
02162   bool      status = false;
02163 
02164   CapabilitiesFile_UnLoad();
02165 
02166   filetxt = new XFILETXT();
02167   if(!filetxt) return false;
02168 
02169   if(filetxt->Open(xpathcapabilitiesfile))
02170     {
02171       filetxt->ReadAllFile();
02172 
02173       XVECTOR<XSTRING*>* lines = filetxt->GetLines();
02174       if(lines)
02175         {
02176           for(XDWORD c=0; c<lines->GetSize(); c++)
02177             {
02178               XSTRING* IDfile    = new XSTRING();
02179               XPATH*   xpathfile = new XPATH();
02180 
02181               if(IDfile && xpathfile)
02182                 {
02183                   XSTRING* line  = lines->Get(c);
02184                   if(line)
02185                     {
02186                       if(!line->IsEmpty())
02187                         {
02188                           if(line->Get()[0] != __C('#'))
02189                             {
02190                               IDfile->AdjustSize(_MAXSTR);
02191                               xpathfile->AdjustSize(_MAXSTR);
02192 
02193                               line->UnFormat(__L("%s:%s"), IDfile->Get(), xpathfile->Get());
02194 
02195                               IDfile->AdjustSize();
02196                               xpathfile->AdjustSize();
02197 
02198                               if(!IDfile->IsEmpty() && !xpathfile->IsEmpty())
02199                                 {
02200                                   capabilitiesfilemap.Add(IDfile, xpathfile);
02201                                   continue;
02202                                 }
02203                             }
02204                         }
02205                     }
02206                 }
02207 
02208               delete IDfile;
02209               delete xpathfile;
02210             }
02211         }
02212 
02213       filetxt->Close();
02214       status = true;
02215     }
02216 
02217   delete filetxt;
02218 
02219   return status;
02220 }
02221 
02222 
02232 bool XFILECOBOL::CapabilitiesFile_UnLoad()
02233 {
02234   if(capabilitiesfilemap.IsEmpty()) return false;
02235 
02236   capabilitiesfilemap.DeleteKeyContents();
02237   capabilitiesfilemap.DeleteElementContents();
02238   capabilitiesfilemap.DeleteAll();
02239 
02240   return true;
02241 }
02242 
02243 
02256 bool XFILECOBOL::CapabilitiesFile_GetFile(XSTRING* fileID, XPATH& xpathfile)
02257 {
02258   if(!fileID) return false;
02259 
02260   xpathfile.Empty();
02261 
02262   for(XDWORD c=0; c<capabilitiesfilemap.GetSize(); c++)
02263     {
02264       if(capabilitiesfilemap.GetKey(c))
02265         {
02266           if(!capabilitiesfilemap.GetKey(c)->Compare(fileID->Get(), true))
02267             {
02268               if(capabilitiesfilemap.GetElement(c))
02269                 {
02270                   xpathfile = capabilitiesfilemap.GetElement(c)->Get();
02271                   break;
02272                 }
02273             }
02274         }
02275     }
02276 
02277   return xpathfile.IsEmpty()?false:true;
02278 }
