02292 bool XFILECOBOL::IsValidCharacter(XCHAR character)
02293 {
02294   bool found = false;
02295 
02296   for(XDWORD c=0; c<validchars.GetSize(); c++)
02297     {
02298       if(validchars.Get()[c] == character) found = true;
02299     }
02300 
02301   return found;
02302 }
