02316 bool XFILECOBOL::Eliminate_InvalidCharacters(XSTRING& line)
02317 {
02318   if(line.IsEmpty()) return false;
02319 
02320   XDWORD c = 0;
02321   while(c<line.GetSize())
02322     {
02323       if(!IsValidCharacter(line.Get()[c]))
02324         {
02325           line.DeleteCharacters(c,1);
02326 
02327         } else c++;
02328     }
02329 
02330   return true;
02331 }
02332 
02333 
02345 bool XFILECOBOL::Eliminate_TabsForSpaces(XVECTOR<XSTRING*>* lines)
02346 {
02347   if(!lines) return false;
02348 
02349   XSTRING tabspaces;
02350 
02351   for(int c=0; c<XFILECOBOL_TABSFORSPACES; c++)
02352     {
02353       tabspaces += __L(" ");
02354     }
02355 
02356   for(XDWORD c=0; c<lines->GetSize(); c++)
02357     {
02358       XSTRING* line = lines->Get(c);
02359       if(line)
02360         {
02361           int index = line->Find(__L("\x09"), true);
02362           if(index != XSTRING_NOTFOUND)
02363             {
02364               line->DeleteCharacters(index, 1);
02365               line->Insert(tabspaces, index);
02366             }
02367         }
02368     }
02369 
02370   return true;
02371 }
02372 
02373 
02385 bool XFILECOBOL::Eliminate_InvalidCharacters(XVECTOR<XSTRING*>* lines)
02386 {
02387   if(!lines) return false;
02388 
02389   for(XDWORD c=0; c<lines->GetSize(); c++)
02390     {
02391       XSTRING* line = lines->Get(c);
02392       if(line) Eliminate_InvalidCharacters((*line));
02393     }
02394 
02395   return true;
02396 }
