02410 bool XFILECOBOL::Eliminate_Comments(XVECTOR<XSTRING*>* lines)
02411 {
02412   if(!lines) return false;
02413 
02414   for(XDWORD c=0; c<lines->GetSize(); c++)
02415     {
02416       XSTRING* line = lines->Get(c);
02417       if(line)
02418         {
02419           int index = line->Find(__L("*"), true);
02420           if(index != XSTRING_NOTFOUND) line->DeleteCharacters(index, line->GetSize()-index);
02421         }
02422     }
02423 
02424   return true;
02425 }
