02454 
02466 bool XFILECOBOL::Eliminate_EmptyLines(XVECTOR<XSTRING*>* lines)
02467 {
02468   if(!lines) return false;
02469 
02470   XDWORD c=0;
02471   while(c<lines->GetSize())
02472     {
02473       XSTRING* line = lines->Get(c);
02474       if(line)
02475         {
02476           line->DeleteCharacter(__C(' '), XSTRINGCONTEXT_FROM_FIRST);
02477           line->DeleteCharacter(__C(' '), XSTRINGCONTEXT_TO_END);
02478           if(line->IsEmpty())
02479             {
02480               delete line;
02481               lines->Delete(line);
02482 
02483             } else c++;
02484         }
02485     }
02486 
02487   return true;
02488 }
