02491 
02503 bool XFILECOBOL::Eliminate_UnnecessarySpaces(XVECTOR<XSTRING*>* lines)
02504 {
02505   if(!lines) return false;
02506 
02507   for(XDWORD c=0; c<lines->GetSize(); c++)
02508     {
02509       XSTRING* line = lines->Get(c);
02510       if(line)
02511         {
02512           XDWORD d = 0;
02513 
02514           while(d<line->GetSize()-1)
02515             {
02516               if((line->Get()[d] == __C(' ')) && (line->Get()[d+1] == __C(' ')))
02517                 {
02518                   line->DeleteCharacters(d, 1);
02519 
02520                 } else d++;
02521             }
02522         }
02523     }
02524 
02525   return true;
02526 }
