02528 
02529 
02541 bool XFILECOBOL::CreateUniqueLines(XVECTOR<XSTRING*>* lines)
02542 {
02543   if(!lines) return false;
02544 
02545   XDWORD c=0;
02546   while(c<lines->GetSize())
02547     {
02548       XSTRING* line = lines->Get(c);
02549       if(line)
02550         {
02551           XSTRING* linenext = lines->Get(c+1);
02552           if((linenext) && (line->Get()[line->GetSize()-1] != __C('.')))
02553             {
02554               line->Add(__C(' '));
02555               line->Add(linenext->Get());
02556 
02557               delete linenext;
02558 
02559               lines->Delete(linenext);
02560 
02561             } else c++;
02562         }
02563     }
02564 
02565   return true;
02566 }
02567 
02568 
02582 bool XFILECOBOL::ExtractReservedToken(XSTRING& line, XCHAR* token, XSTRING& var)
02583 {
02584   XSTRING mask;
02585   XSTRING strvar;
02586 
02587   mask.Format(__L("%s "), token);
02588 
02589   int start         = line.Find(mask.Get(), true);
02590   int startendtoken = 0;
02591   int end           = 0;
02592   if(start == XSTRING_NOTFOUND)  return false;
02593 
02594   startendtoken = start + mask.GetSize();
02595 
02596   end = line.Find(__L(" "), true, startendtoken);
02597   if(end == XSTRING_NOTFOUND) end = line.Find(__L("."), true, startendtoken);
02598   if(end == XSTRING_NOTFOUND) return false;
02599 
02600   line.Copy(startendtoken, end, var);
02601   line.DeleteCharacters(start, (end-start));
02602 
02603   return true;
02604 }
02605 
02606 
02607 
02620 bool XFILECOBOL::ExtractReservedToken(XSTRING& line, XCHAR* token)
02621 {
02622   XSTRING mask;
02623   XSTRING strvar;
02624 
02625   mask = token;
02626 
02627   int start         = line.Find(mask.Get(), true);
02628   int end           = 0;
02629   if(start == XSTRING_NOTFOUND)  return false;
02630 
02631   end = start + mask.GetSize();
02632   line.DeleteCharacters(start, (end-start));
02633 
02634   return true;
02635 }
02636 
02637 
02638 
02658 XFILECOBOL_LINE_TYPE XFILECOBOL::InterpreterLineForHandle(XSTRING& line, int& level, XFILECOBOL_FIELD_TYPE& fieldtype, XFILECOBOL_HANDLE& handle, XSTRING& name, XSTRING& param, XSTRING& param2, int& noccurs, int& PICcomputationaltype)
02659 {
02660   XFILECOBOL_LINE_TYPE  returntype = XFILECOBOL_LINE_TYPE_UNKNOWN;
02661   int                   index      = -1;
02662 
02663   level                 = 0;
02664   fieldtype             =  XFILECOBOL_FIELD_TYPE_UNKNOWN;
02665   PICcomputationaltype  = 0;
02666 
02667   //----------------------------------------------------------
02668   // FD
02669   index = line.Find(__L("FD "), true);
02670   if(!index)
02671     {
02672       ExtractReservedToken(line, __L("VALUE OF LABEL IS"), (*handle.GetValueLabel()));
02673       param.DeleteCharacter(__C('\''), XSTRINGCONTEXT_ALLSTRING);
02674 
02675       ExtractReservedToken(line, __L("DATA RECORD IS"), (*handle.GetDataRecord()));
02676       ExtractReservedToken(line, __L("FD"), (*handle.GetName()));
02677 
02678       returntype = XFILECOBOL_LINE_TYPE_FD;
02679     }
02680    else
02681     {
02682       index = line.Find(__L("SELECT "), true);
02683       if(!index)
02684         {
02685           ExtractReservedToken(line, __L("SELECT")          , name);
02686           ExtractReservedToken(line, __L("ORGANIZATION IS") , param);
02687           ExtractReservedToken(line, __L("ASSIGN TO")       , param2);
02688 
02689           returntype = XFILECOBOL_LINE_TYPE_SELECT;
02690         }
02691        else
02692         {
02693           //----------------------------------------------------------
02694           // FIELD LEVEL
02695 
02696           XSTRING levelstring;
02697 
02698           line.Copy(0, 2, levelstring);
02699 
02700           level = levelstring.ConvertToInt();
02701           if(level)
02702             {
02703               noccurs = 0;
02704 
02705               ExtractReservedToken(line, __L(" TIMES"));
02706               ExtractReservedToken(line, __L("OCCURS"), param2);
02707               if(!param2.IsEmpty())
02708                 {
02709                   param2.UnFormat(__L("%d"), &noccurs);
02710                   param2.Empty();
02711                 }
02712 
02713               ExtractReservedToken(line, __L("REDEFINES"), param2);
02714 
02715               if(line.Find(__L("PIC "), true) != XSTRING_NOTFOUND)
02716                 {
02717                   if(ExtractReservedToken(line, __L("COMP-1")))  PICcomputationaltype = 1;
02718                   if(ExtractReservedToken(line, __L("COMP-2")))  PICcomputationaltype = 2;
02719                   if(ExtractReservedToken(line, __L("COMP-3")))  PICcomputationaltype = 3;
02720                   if(ExtractReservedToken(line, __L("COMP-4")))  PICcomputationaltype = 4;
02721                   if(ExtractReservedToken(line, __L("COMP-5")))  PICcomputationaltype = 5;
02722 
02723                   ExtractReservedToken(line, __L("PIC"), param);
02724 
02725                   name.AdjustSize(_MAXSTR);
02726                   line.UnFormat(__L("%02d %s"), &level, name.Get());
02727                   name.AdjustSize();
02728 
02729                   fieldtype =  XFILECOBOL_FIELD_TYPE_VAR;
02730                 }
02731                else
02732                 {
02733                   param.Empty();
02734 
02735                   name.AdjustSize(_MAXSTR);
02736                   line.UnFormat(__L("%02d %s."), &level, name.Get());
02737                   name.AdjustSize();
02738 
02739                   fieldtype =  XFILECOBOL_FIELD_TYPE_KEY;
02740                 }
02741 
02742               returntype = XFILECOBOL_LINE_TYPE_FIELD;
02743             }
02744 
02745            name.DeleteCharacter(__C(' '));
02746            param.DeleteCharacter(__C(' '));
02747            param2.DeleteCharacter(__C(' '));
02748 
02749            name.DeleteCharacter(__C('.'));
02750            param.DeleteCharacter(__C('.'));
02751            param2.DeleteCharacter(__C('.'));
02752         }
02753     }
02754 
02755   return returntype;
02756 }
02757 
02758 
02770 XFILECOBOL_FD_TREENODE* XFILECOBOL::GetParentNodeOfLevel(int level)
02771 {
02772   if(!handle.GetFDTree()) return NULL;
02773 
02774   XFILECOBOL_FD_TREENODEITERATOR  it_start(handle.GetFDTree()->Begin());
02775   XFILECOBOL_FD_TREENODEITERATOR  it_end(handle.GetFDTree()->End());
02776 
02777   while(it_start != it_end)
02778     {
02779       XFILECOBOL_FIELD* fieldtmp  = (*it_start);
02780       if(!fieldtmp) break;
02781 
02782       if(fieldtmp->GetLevel() == level)
02783         {
02784           return it_start.GetCurrent()->GetParent();
02785         }
02786 
02787       ++it_start;
02788     }
02789 
02790   return NULL;
02791 }
02792 
02793 
02806 bool XFILECOBOL::TraslateTextToHandle(XVECTOR<XSTRING*>* lines, XFILECOBOL_HANDLE& handle)
02807 {
02808   if(!lines) return false;
02809 
02810   Eliminate_TabsForSpaces(lines);
02811   Eliminate_InvalidCharacters(lines);
02812   Eliminate_Comments(lines);
02813   Eliminate_NumberLines(lines);
02814   Eliminate_EmptyLines(lines);
02815   Eliminate_UnnecessarySpaces(lines);
02816 
02817   CreateUniqueLines(lines);
02818 
02819   XFILECOBOL_FD_TREENODE* actualfieldnode = NULL;
02820   int                     actuallevel     = 0;
02821 
02822   for(XDWORD c=0; c<lines->GetSize(); c++)
02823     {
02824       XFILECOBOL_LINE_TYPE    linetype          = XFILECOBOL_LINE_TYPE_UNKNOWN;
02825       int                     level             = 0;
02826       XFILECOBOL_FIELD_TYPE   fieldtype         = XFILECOBOL_FIELD_TYPE_UNKNOWN;
02827       XSTRING                 name;
02828       XSTRING                 param;
02829       XSTRING                 param2;
02830       int                     noccurs                = 0;
02831       int                     PICcomputationaltype   = 0;
02832 
02833       linetype = InterpreterLineForHandle((*lines->Get(c)), level, fieldtype, handle, name, param, param2, noccurs, PICcomputationaltype);
02834 
02835       switch(linetype)
02836         {
02837           case XFILECOBOL_LINE_TYPE_FD          : { XFILECOBOL_FIELD* rootfielddata = new XFILECOBOL_FIELD();
02838                                                     if(!rootfielddata) return false;
02839 
02840                                                     XFILECOBOL_FD_TREENODE* rootfieldnode = new XFILECOBOL_FD_TREENODE(rootfielddata);
02841                                                     if(!rootfieldnode)
02842                                                       {
02843                                                         delete rootfielddata;
02844                                                         return false;
02845                                                       }
02846 
02847                                                     rootfielddata->GetName()->Set(__L("ROOT"));
02848                                                     rootfielddata->SetLevel(0);
02849 
02850                                                     handle.GetFDTree()->SetRoot(rootfieldnode);
02851                                                     actualfieldnode = rootfieldnode;
02852 
02853                                                     actuallevel     = 1;
02854                                                   }
02855                                                   break;
02856 
02857           case XFILECOBOL_LINE_TYPE_SELECT      : { XPATH xpathdata;
02858 
02859                                                     if(handle.GetName()->Compare(name, true))
02860                                                       {
02861                                                         return false;
02862                                                       }
02863 
02864                                                     if(!param.IsEmpty())
02865                                                       {
02866                                                         if(!param.Compare(__L("INDEXED")         , true)) handle.SetTypeRecord(XFILECOBOL_RECORD_TYPE_INDEXED);
02867                                                         if(!param.Compare(__L("LINE SEQUENTIAL") , true)) handle.SetTypeRecord(XFILECOBOL_RECORD_TYPE_LINE_SEQUENTIAL);
02868                                                         if(!param.Compare(__L("SEQUENTIAL")      , true)) handle.SetTypeRecord(XFILECOBOL_RECORD_TYPE_SEQUENTIAL);
02869                                                         if(!param.Compare(__L("RELATIVE")        , true)) handle.SetTypeRecord(XFILECOBOL_RECORD_TYPE_RELATIVE);
02870                                                       }
02871 
02872                                                     if(!param2.IsEmpty())
02873                                                       {
02874                                                         if(!param2.Compare(__L("DISK"), true))
02875                                                           {
02876                                                             CapabilitiesFile_GetFile(handle.GetName(), xpathdata);
02877                                                             xpathdata.SetOnlyPathAndNamefileExt();
02878                                                           }
02879                                                          else
02880                                                           {
02881                                                             xpathdata.AdjustSize(_MAXSTR);
02882 
02883                                                             if(param2.Find(__L("'") , true) != XSTRING_NOTFOUND)  param2.UnFormat(__L("'%s'")  , xpathdata.Get());
02884                                                             if(param2.Find(__L("\""), true) != XSTRING_NOTFOUND)  param2.UnFormat(__L("\"%s\""), xpathdata.Get());
02885 
02886                                                             xpathdata.AdjustSize();
02887                                                           }
02888                                                       }
02889 
02890                                                     if(xpathdatafile.IsEmpty())
02891                                                       {
02892                                                         xpathdatafile = xpathdata.Get();
02893                                                       }
02894 
02895                                                   }
02896 
02897                                                   break;
02898 
02899 
02900           case XFILECOBOL_LINE_TYPE_FIELD       : { XFILECOBOL_FIELD*       fielddata = NULL;
02901                                                     XFILECOBOL_FD_TREENODE* fieldnode = NULL;
02902 
02903                                                     fielddata = new XFILECOBOL_FIELD();
02904                                                     if(fielddata)
02905                                                       {
02906                                                         fieldnode = new XFILECOBOL_FD_TREENODE(fielddata);
02907                                                         if(!fieldnode) break;
02908 
02909                                                       } else break;
02910 
02911                                                     if(fieldnode)
02912                                                       {
02913                                                         fieldnode->GetData()->SetType(fieldtype);
02914                                                         fieldnode->GetData()->SetLevel(level);
02915                                                         fieldnode->GetData()->GetName()->Set(name);
02916                                                         fieldnode->GetData()->GetPIC()->GetMask()->Set(param);
02917                                                         fieldnode->GetData()->GetPIC()->SetComputationalType(PICcomputationaltype);
02918                                                         fieldnode->GetData()->SetNOccurs(noccurs);
02919 
02920                                                         if(param2.GetSize())
02921                                                           {
02922                                                             fieldnode->GetData()->SetIsRedefine(true);
02923                                                             fieldnode->GetData()->GetRedefineName()->Set(param2);
02924                                                           }
02925                                                          else
02926                                                           {
02927                                                             fieldnode->GetData()->SetIsRedefine(false);
02928                                                           }
02929 
02930                                                         if(fieldnode->GetData()->GetType() == XFILECOBOL_FIELD_TYPE_VAR)
02931                                                           {
02932                                                             fieldnode->GetData()->GetPIC()->ResolveMask();
02933                                                           }
02934 
02935                                                         if(level == actuallevel)
02936                                                           {
02937                                                             actualfieldnode->AddChild(fieldnode);
02938                                                           }
02939                                                           else
02940                                                           {
02941                                                             if((level > actuallevel))
02942                                                               {
02943                                                                 actualfieldnode->AddChild(fieldnode);
02944                                                                 actuallevel     = level;
02945                                                               }
02946                                                              else
02947                                                               {
02948                                                                 actualfieldnode = GetParentNodeOfLevel(level);
02949                                                                 if(!actualfieldnode)
02950                                                                   {
02951                                                                     actualfieldnode = handle.GetFDTree()->GetRoot();
02952                                                                     break;
02953                                                                   }
02954 
02955                                                                 /*
02956                                                                 for(int d=0; d<(actuallevel-level); d++)
02957                                                                   {
02958                                                                     actualfieldnode = actualfieldnode->GetParent();
02959                                                                     if(!actualfieldnode)
02960                                                                       {
02961                                                                         actualfieldnode = handle.GetFDTree()->GetRoot();
02962                                                                         break;
02963                                                                       }
02964                                                                   }
02965                                                                 */
02966 
02967                                                                 actualfieldnode->AddChild(fieldnode);
02968                                                                 actuallevel = level;
02969                                                               }
02970                                                           }
02971 
02972                                                         if(fieldtype == XFILECOBOL_FIELD_TYPE_KEY)
02973                                                           {
02974                                                             actualfieldnode = fieldnode;
02975                                                           }
02976                                                       }
02977                                                   }
02978 
02979                                                   break;
02980 
02981         }
02982     }
02983 
02984   // -----------------------------------------------------------------------------
02985   // Recreate the nodes with "Occurs" in Cobol
02986   // Root is special usually only has one child, but it may not ...
02987 
02988   XFILECOBOL_FD_TREENODE* node = handle.GetFDTree()->GetRoot();
02989   if(node)
02990     {
02991       for(XDWORD c=0; c<(XDWORD)node->GetNChildren(); c++)
02992         {
02993           XFILECOBOL_FD_TREENODE* subnode = node->GetChild(c);
02994           if(subnode)
02995             {
02996               if(subnode->GetData())
02997                 {
02998                   AjustNodeWithOccurs(subnode);
02999                 }
03000             }
03001         }
03002     }
03003 
03004   return true;
03005 }
03006 
03007 
03008 
03021 bool XFILECOBOL::ChangeNameNodeWithOccurs(int index, XSTRING* name)
03022 {
03023   if(!name)             return false;
03024   if(name->IsEmpty())   return false;
03025 
03026   int indexstart = name->Find(__L("("), true);
03027   if(indexstart == XSTRING_NOTFOUND)
03028     {
03029       name->AddFormat(__L("(%d)"), index);
03030     }
03031    else
03032     {
03033       int indexend = name->Find(__L(")"), true);
03034       if(indexend == XSTRING_NOTFOUND) return false;
03035 
03036       XSTRING addindexstr;
03037       addindexstr.Format(__L(",%d"), index);
03038 
03039       name->Insert(addindexstr, indexend);
03040     }
03041 
03042   return true;
03043 }
03044 
03045 
03046 
03060 XFILECOBOL_FD_TREENODE* XFILECOBOL::DuplicateNodeWithSubnodes(int index, XFILECOBOL_FD_TREENODE* node, XFILECOBOL_FD_TREENODE* cretednode)
03061 {
03062   XFILECOBOL_FIELD*       newfielddata = NULL;
03063   XFILECOBOL_FD_TREENODE* newfieldnode = NULL;
03064   XSTRING                 namesubnode;
03065 
03066   namesubnode =  node->GetData()->GetName()->Get();
03067   if(!ChangeNameNodeWithOccurs(index, &namesubnode)) return NULL;
03068 
03069   newfielddata = new XFILECOBOL_FIELD();
03070   if(newfielddata)
03071     {
03072       newfieldnode = new XFILECOBOL_FD_TREENODE(newfielddata);
03073       if(!newfieldnode) return NULL;
03074 
03075     } else return NULL;
03076 
03077   newfieldnode->GetData()->SetType(node->GetData()->GetType());
03078   newfieldnode->GetData()->SetLevel(node->GetData()->GetLevel());
03079   newfieldnode->GetData()->GetName()->Set(namesubnode);
03080   newfieldnode->GetData()->GetPIC()->GetMask()->Set(node->GetData()->GetPIC()->GetMask()->Get());
03081   newfieldnode->GetData()->GetPIC()->SetComputationalType(node->GetData()->GetPIC()->GetComputationalType());
03082   newfieldnode->GetData()->SetNOccurs(node->GetData()->GetNOccurs());
03083 
03084   if(newfieldnode->GetData()->GetType() == XFILECOBOL_FIELD_TYPE_VAR)
03085     {
03086       newfieldnode->GetData()->GetPIC()->ResolveMask();
03087     }
03088 
03089  if(!cretednode)
03090     {
03091       int indexnode = handle.GetIndexNodeForName(node->GetParent(), node->GetData()->GetName()->Get());
03092       if(indexnode != NOTFOUND)
03093              node->GetParent()->InsertChild(indexnode, newfieldnode);
03094        else  node->GetParent()->AddChild(newfieldnode);
03095     }
03096    else
03097     {
03098       cretednode->AddChild(newfieldnode);
03099     }
03100 
03101   for(XDWORD c=0; c<(XDWORD)node->GetNChildren(); c++)
03102     {
03103       XFILECOBOL_FD_TREENODE* subnode = node->GetChild(c);
03104       if(subnode) DuplicateNodeWithSubnodes(index, subnode, newfieldnode);
03105     }
03106 
03107   return newfieldnode;
03108 }
03109 
03110 
03111 
03112 
03124 bool XFILECOBOL::AjustNodeWithOccurs(XFILECOBOL_FD_TREENODE* node)
03125 {
03126   if(!node)            return false;
03127   if(!node->GetData()) return false;
03128 
03129   XDWORD noccurs = node->GetData()->GetNOccurs();
03130   if(noccurs)
03131     {
03132       XFILECOBOL_FD_TREENODE*  newnode  = NULL;
03133       XFILECOBOL_FD_TREENODE*  parent   = node->GetParent();
03134 
03135       node->GetData()->SetNOccurs(0);
03136 
03137       for(XDWORD c=0; c<(XDWORD)noccurs; c++)
03138         {
03139           newnode = DuplicateNodeWithSubnodes(c+1, node);
03140           if(newnode)
03141             {
03142               for(XDWORD c=0; c<(XDWORD)newnode->GetNChildren(); c++)
03143                 {
03144                   XFILECOBOL_FD_TREENODE* subnode = newnode->GetChild(c);
03145                   if(subnode) AjustNodeWithOccurs(subnode);
03146                 }
03147             }
03148         }
03149 
03150       if(parent) parent->DelChild(node);
03151     }
03152    else
03153     {
03154       for(XDWORD c=0; c<(XDWORD)node->GetNChildren(); c++)
03155         {
03156           XFILECOBOL_FD_TREENODE* subnode = node->GetChild(c);
03157           if(subnode) AjustNodeWithOccurs(subnode);
03158         }
03159     }
03160 
03161   return true;
03162 }
