03175 void XFILECOBOL::Clean()
03176 {
03177   havefileheaders     = false;
03178   type                = XFILECOBOL_FILE_TYPE_UNKNOWN;
03179   fileheadersize      = 0;
03180 
03181   xfiledata           = NULL;
03182 
03183   sizedatarecord      = 0;
03184   maxfilerecordsize   = 0;
03185   minfilerecordsize   = 0;
03186 }
