00591 bool XFILEDBF_HEADER::Load(XFILE* xfile)
00592 {
00593   // ---------------------------
00594   // Read header
00595   if(!xfile->SetPosition(8)) return false;
00596 
00597   if(!xfile->Read((XBYTE*)&headersize,2)) return false;
00598   if(!headersize) return 0;
00599 
00600   if(headerdata)
00601     {
00602       delete[] headerdata;
00603       headerdata = NULL;
00604     }
00605 
00606   headerdata = new XBYTE[headersize];
00607   if(!headerdata) return false;
00608 
00609   if(!xfile->SetPosition(0)) return false;
00610   if(!xfile->Read(headerdata,headersize)) return false;
00611 
00612   int index=XFILEDBF_FIELDSTART;
00613 
00614   while(headerdata[index]!=0x0D)
00615     {
00616       XFILEDBF_FIELDTYPE type;
00617 
00618       switch(headerdata[index+11])
00619         {
00620           case 'C' : type=XFILEDBF_FIELDTYPE_CHARACTER; break;
00621           case 'D' : type=XFILEDBF_FIELDTYPE_DATE;      break;
00622           case 'F' : type=XFILEDBF_FIELDTYPE_FLOATING;  break;
00623           case 'N' : type=XFILEDBF_FIELDTYPE_BINARYHEX; break;
00624           case 'L' : type=XFILEDBF_FIELDTYPE_LOGICAL;   break;
00625           case 'M' : type=XFILEDBF_FIELDTYPE_MEMO;      break;
00626           case 'V' : type=XFILEDBF_FIELDTYPE_VARIABLE;  break;
00627           case 'P' : type=XFILEDBF_FIELDTYPE_PICTURE;   break;
00628           case 'B' : type=XFILEDBF_FIELDTYPE_BINARY;    break;
00629           case 'G' : type=XFILEDBF_FIELDTYPE_GENERAL;   break;
00630           case '2' : type=XFILEDBF_FIELDTYPE_SHORTINT;  break;
00631           case '4' : type=XFILEDBF_FIELDTYPE_LONGINT;   break;
00632           case '8' : type=XFILEDBF_FIELDTYPE_DOUBLE;    break;
00633           default  : type=XFILEDBF_FIELDTYPE_UNKNOWN;   break;
00634         }
00635 
00636       if(type!=XFILEDBF_FIELDTYPE_UNKNOWN)
00637         {
00638           XSTRING name((char*)&headerdata[index]);
00639 
00640           if(!CreateField(type, name.Get(), headerdata[index+16])) return false;
00641         }
00642 
00643       index+=XFILEDBF_FIELDSIZE;
00644     }
00645 
00646   return true;
00647 }
