01027           case XFILEDBF_FIELDTYPE_CHARACTER : field->size=0;   break;
01028           case XFILEDBF_FIELDTYPE_DATE      : field->size=0;   break;
01029           case XFILEDBF_FIELDTYPE_FLOATING  : field->size=32;  break;
01030           case XFILEDBF_FIELDTYPE_BINARYHEX : field->size=32;  break;
01031           case XFILEDBF_FIELDTYPE_LOGICAL   : field->size=0;   break;
01032           case XFILEDBF_FIELDTYPE_MEMO      : field->size=0;   break;
01033           case XFILEDBF_FIELDTYPE_VARIABLE  : break;
01034           case XFILEDBF_FIELDTYPE_PICTURE   : break;
01035           case XFILEDBF_FIELDTYPE_BINARY    : break;
01036           case XFILEDBF_FIELDTYPE_GENERAL   : break;
01037           case XFILEDBF_FIELDTYPE_SHORTINT  : break;
01038           case XFILEDBF_FIELDTYPE_LONGINT   : break;
01039           case XFILEDBF_FIELDTYPE_DOUBLE    : break;
01040         }
01041     }
