01355 XFILEDBF_RECORD* XFILEDBF::ReadRecord(XDWORD nrecord)
01356 {
01357   if(!IsOpen()) return NULL;
01358   if(!header)   return NULL;
01359 
01360   XFILEDBF_RECORD* record;
01361   if(nrecord>=header->GetNRecords()) return NULL;
01362 
01363   XDWORD sizerecord = header->GetSizeRecord();
01364 
01365   if(!sizerecord) return NULL;
01366 
01367   record = new XFILEDBF_RECORD(header->GetFields());
01368   if(!record) return NULL;
01369 
01370   XBYTE* data = new XBYTE [sizerecord];
01371   if(!data)
01372     {
01373       delete record;
01374       return NULL;
01375     }
01376 
01377   int position=header->GetHeaderSize()+(sizerecord*nrecord);
01378 
01379   file->SetPosition(position);
01380 
01381   if(!file->Read(data,sizerecord))
01382     {
01383       delete record;
01384       delete[] data;
01385 
01386       return NULL;
01387     }
01388 
01389   record->SetData(data,sizerecord);
01390 
01391   delete[] data;
01392 
01393   return record;
01394 }
